    [Form]
    public class PlSADTable extends FormRun
    {
        NumberSeq       numberSeq;
        PlSadNumberCode sadCode;
        QueryBuildRange queryFilter;
        NoYes           showPosted;
        boolean         inPosting;
        boolean isTaxIntegrationEnabled = Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::SADDocument);
}

public void applyHeaderFilter()
{
    if (inPosting && ! showPosted)
    {
        queryFilter.value('((' + fieldStr(PlSADTable, Posted) + '=0' + ')||(' + fieldStr(PlSADTable, RecId) + '=' + int642str(sadTable.RecId) + '))');
    }
    else
    {
        queryFilter.value(
            showPosted                 ?
            SysQuery::valueUnlimited() :
            queryValue(NoYes::No));
    }
}

void buttonShow()
{
    if (sadTable.Voucher)
    {
        Voucher.enabled(true);
        SADReference.enabled      (false);
        Invoice.enabled           (false);

        PlSADPurchLineCreate.enabled(false);
    }
    else
    {
        Voucher.enabled(false);
        if (sadTable.RecId)
        {
            SADReference.enabled  (true);
            Invoice.enabled       (true);

            PlSADPurchLineCreate.enabled(true);
        }
        else
        {
            SADReference.enabled  (false);
            Invoice.enabled       (false);

            PlSADPurchLineCreate.enabled(false);
        }
    }
    if (isTaxIntegrationEnabled)
    {
        if (sadTable.Voucher)
        {
            TaxTransSource.enabled(false);
            LineTaxTransSource.enabled(false);
        }
        else
        {
            TaxTransSource.enabled(true);
            LineTaxTransSource.enabled(true);
        }
    }
}

public void init()
{
    super();

    this.buttonShow();
    if (isTaxIntegrationEnabled)
    {
        TaxTransSource.visible(true);
        LineTaxTransSource.visible(true);
        SADTrans_ds.object(fieldNum(PlSADTrans, TaxGroup)).visible(true);
        SADTrans_ds.object(fieldNum(PlSADTrans, TaxItemGroup)).visible(true);

        SADTrans_ds.object(fieldNum(PlSADTrans, TaxAmount)).visible(false);
        SADTrans_ds.object(fieldNum(PlSADTrans, TaxCode)).visible(false);
    }
}

public void recalcSADTrans()
{
    PlSADRateGroup  sadRateGroup;
    PlSADParameters sadParameters;

    sadRateGroup  = PlSadRateGroup::find(sadTrans.sadRateCode);
    sadParameters = PlSADParameters::find();

    sadTrans.sadDutyAmount1 = sadTrans.sadBaseAmount                             * sadRateGroup.sadDuty1Rate / 100;
    sadTrans.sadDutyAmount1 = round(sadTrans.sadDutyAmount1, sadParameters.sadDuty1RoundOff);

    sadTrans.sadDutyAmount2 = (sadTrans.sadBaseAmount + sadTrans.sadDutyAmount1) * sadRateGroup.sadDuty2Rate / 100;
    sadTrans.sadDutyAmount2 = round(sadTrans.sadDutyAmount2, sadParameters.sadDuty2RoundOff);

    sadTrans.TaxCode        = sadRateGroup.VatCode;
    if (element.isTaxIntegrationEnabled)
    {
        SADTrans.TaxGroup = sadRateGroup.TaxGroup;
        SADTrans.TaxItemGroup = sadRateGroup.TaxItemGroup;
    }
    else
    {
        sadTrans.TaxAmount      = (sadTrans.sadBaseAmount + sadTrans.sadDutyAmount1 + sadTrans.sadDutyAmount2) *
                                    TaxData::percent(sadTrans.TaxCode, sadTable.TransDate, sadTrans.sadBaseAmount + sadTrans.sadDutyAmount1 + sadTrans.sadDutyAmount2) / 100;
        sadTrans.TaxAmount      = round(sadTrans.TaxAmount, sadParameters.VatAmountRoundOff);
    }
    sadTrans_ds.refresh();
}

edit NoYes showPosted(boolean _set, NoYes _showPosted = showPosted)
{
    if (_set)
    {
        showPosted = _showPosted;
        sadTable_ds.executeQuery();
    }

    return showPosted;
}

void vatUpdate()
{
    sadTrans.TaxAmount = (sadTrans.sadBaseAmount + sadTrans.sadDutyAmount1 + sadTrans.sadDutyAmount2 + sadTrans.sadTransAmount) *
                          TaxData::percent(sadTrans.TaxCode, sadTable.TransDate, sadTrans.sadBaseAmount + sadTrans.sadDutyAmount1 + sadTrans.sadDutyAmount2) / 100;
    sadTrans.TaxAmount = round(sadTrans.TaxAmount, PlSadParameters::find().VatAmountRoundOff);
}

public int active()
{
    int ret = super();

    element.buttonShow();
    this.allowEdit(!sadTable.Voucher);
    this.allowDelete(!sadTable.Voucher);
    invoice.enabled(PlSadTable::postButtonActive(sadTable.SadNumberCode));

    return ret;
}

public void create(boolean _append = false)
{
    super(_append);

    numberSeq = NumberSeq::newGetNum(PlSadParameters::numRefSadNumberCode(), true, true);
    if (numberSeq)
    {
        sadTable.SadNumberCode    = numberSeq.num();
        sadCode                   = sadTable.SadNumberCode;
    }
}

public void delete()
{
    ttsbegin;

    if (!sadTable.RecId && numberSeq)
    {
        numberSeq.abort();
    }

    super();

    ttscommit;
}

public void executeQuery()
{
    element.applyHeaderFilter();

    super();
}

public void init()
{
    super();

    queryFilter = this.query().dataSourceNo(1).addRange(fieldnum(PlSADTable, Posted));
}

public void write()
{
    ttsbegin;

    if (numberSeq && sadTable.SadNumberCode == sadCode)
    {
        numberSeq.used();
    }
    else
    {
        if (numberSeq)
        {
            numberSeq.abort();
        }
    }

    ttscommit;

    super();
}

public boolean validate()
{
    boolean         ret = super();
    PlSadInvoice    sadInvoice;
    PurchTable      purchTable;

    if (sadTable.CurrencyCode == CompanyInfoHelper::standardCurrency())
    {
        ret = checkFailed("@GLS110450");
    }

    select firstonly sadInvoice
        where sadInvoice.sadNumberCode == sadTable.SadNumberCode
    exists join purchTable
        where purchTable.PurchId      == sadInvoice.PurchId &&
              purchTable.CurrencyCode != sadTable.CurrencyCode;

    if (sadInvoice)
    {
        ret = checkFailed("@GLS109528");
    }

    return ret;
}

public void modified()
{
    super();

    if (sadTable.CurrencyCode != CompanyInfoHelper::standardCurrency())
    {
        sadTable.ExchRate = ExchangeRateHelper::exchRate(sadTable.CurrencyCode, sadTable.SadDate);
        sadTable_ds.refresh();
    }
}

public void modified()
{
    super();

    if (!isTaxIntegrationEnabled)
    {
        return;
    }
    if (TaxSpecificExchangeRateHelper::isTaxSpecificExchRateEnabledByBusinessProcess(TaxIntegrationBusinessProcess::SADDocument))
    {
        TaxSpecificExchangeRate::deleteBySourceRecord(sadTable.TableId, sadTable.RecId);
    }
    // No need to handle bank exchange rate because transaction currency equals to accounting currency.
}

