public void lookup(FormControl _formControl, str _filterStr)
{
    if (!isTaxIntegrationEnabled)
    {
        super(_formControl, _filterStr);
        return;
    }
    Query query = new Query();
    query.addDataSource(tableNum(PlSADRateGroup));

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(PlSADRateGroup), _formControl);
    sysTableLookup.addLookupField(fieldNum(PlSADRateGroup, SADRateCode), true);
    sysTableLookup.addLookupField(fieldNum(PlSADRateGroup, TaxGroup));
    sysTableLookup.addLookupField(fieldNum(PlSADRateGroup, TaxItemGroup));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

