[Form]
public class PlanActivity extends FormRun implements PlanIPlanForm
{
    CompanyInfo             companyInfo;
    Plan                    callerPlan;
    QueryBuildRange         predecessorActivityRange;
    QueryBuildRange         successorActivityRange;
    QueryBuildRange         successorProdFlowRelationRange;
    QueryBuildRange         successorLeanProductionFlowReference;

    QueryBuildDataSource    qbdsLeanProductionFlow;
}

public void init()
{
    callerPlan = element.args().record();

    super();

    companyInfo    = CompanyInfo::find();

    this.setRelationsFilter();
}

public Plan plan()
{
    return plan.data();
}

/// <summary>Refreshes content of the form.</summary>
/// <parameter name="_planActivityToSelect">Sets focus on this planned activity after the refresh.</parameter>
public void refreshForm(PlanActivity _planActivityToSelect = null)
{
    if (_planActivityToSelect)
    {
        element.args().lookupRecord(_planActivityToSelect);
    }
    planActivity_ds.research(false);
}

public void refreshRelations()
{
    planActivityRelationshipSucessor_ds.executeQuery();
    planActivityRelationshipPredecessor_ds.executeQuery();
}

public void setFormControls()
{
    boolean isCurrentLegalEntity = callerPlan.legalEntity() == companyInfo.RecId;

    // set data sources and 'edit' type menu items to read-only if the selected production flow belongs to a different legal entity
    planActivity_ds.allowDelete(isCurrentLegalEntity);
    planActivityRelationshipSucessor_ds.allowEdit(isCurrentLegalEntity);
    planActivityRelationshipSucessor_ds.allowDelete(isCurrentLegalEntity);
    planActivityRelationshipPredecessor_ds.allowEdit(isCurrentLegalEntity);
    planActivityRelationshipPredecessor_ds.allowDelete(isCurrentLegalEntity);
    leanProdFlowPlanActivityRelationSucessor_ds.allowEdit(isCurrentLegalEntity);
    leanProdFlowPlanActivityRelationSucessor_ds.allowDelete(isCurrentLegalEntity);
    leanProdFlowPlanActivityRelationPred_ds.allowEdit(isCurrentLegalEntity);
    leanProdFlowPlanActivityRelationPred_ds.allowDelete(isCurrentLegalEntity);
    planPlanActivity_ds.allowEdit(isCurrentLegalEntity);
    planPlanActivity_ds.allowDelete(isCurrentLegalEntity);

    actionButtonGroup.enabled(isCurrentLegalEntity);
    predecessorButtonGroup.enabled(isCurrentLegalEntity);
    successorButtonGroup.enabled(isCurrentLegalEntity);
}

/// <summary>
/// Applies filters for the Successors and Predecessors views.
/// </summary>
public void setRelationsFilter()
{
    boolean isBridge = LeanProductionFlowActivity::findPlanActivity(planActivity.RecId).FeederBridge == NoYes::Yes;
    str     filterValue1;
    str     filterValue2;

    successorActivityRange.value(queryValue(planActivity.RecId));
    predecessorActivityRange.value(queryValue(planActivity.RecId));

    if (isBridge)
    {
        filterValue1 = queryValue(SysQuery::valueEmptyString());
        filterValue2 = strFmt('((%1.%2 == %3) || (%4.%5 != %6))',
                                qbdsLeanProductionFlow.name(),
                                fieldStr(LeanProductionFlow, Plan),
                                callerPlan.RecId,
                                qbdsLeanProductionFlow.name(),
                                fieldStr(LeanProductionFlow, ProductionFlowReference),
                                LeanProductionFlowReference::findPlanReference(callerPlan.PlanReference).RecId);
    }
    else
    {
        filterValue1 = queryValue(LeanProductionFlow::findPlan(callerPlan.RecId).RecId);
        filterValue2 = queryValue(SysQuery::valueEmptyString());
    }

    successorProdFlowRelationRange.value(filterValue1);
    successorLeanProductionFlowReference.value(filterValue2);

    element.refreshRelations();
}

public void executeQuery()
{
    super();

    planActivityDetails.enabled(planActivity.RecId != 0);
    planActivityRelationNewPred.enabled(planActivity.RecId != 0);
    planActivityRelationNewSucessor.enabled(planActivity.RecId != 0);
}

public void delete()
{
    ttsbegin;

    planPlanActivity.delete();

    ttscommit;

    planActivity_ds.research();
}

public int active()
{
    int result = super();

    element.setFormControls();
    element.setRelationsFilter();

    return result;
}

public void init()
{
    QueryBuildRange qbr;
    super();

    qbr = this.query().dataSourceTable(tableNum(PlanActivity)).addRange(fieldNum(PlanActivity, PlanActivityType));
    qbr.status(RangeStatus::Locked);
    qbr.value(strFmt("@SYS26868", queryValue(PlanActivityType::Transfer), queryValue(PlanActivityType::Process)));
}

public void delete()
{
    ttsbegin;
    leanProdFlowPlanActivityRelationSucessor.delete();
    ttscommit;
    planActivityRelationshipSucessor_DS.research();
}

public void init()
{
    QueryBuildDataSource qbds;
    super();

    qbds = this.query().dataSourceName(this.name());
    successorActivityRange = qbds.addRange(fieldNum(PlanActivityRelationship, IndependentPlanActivity));
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceName(this.name());
    qbds.clearLinks();

    qbds.addLink(fieldNum(PlanActivityRelationship, DependentPlanActivity), fieldNum(PlanActivity, RecId));

    qbds.addRange(fieldNum(PlanActivity, PlanActivityType)).value(
                    strFmt("@SYS26868",
                            SysQuery::value(enum2int(PlanActivityType::Process)),
                            SysQuery::value(enum2int(PlanActivityType::Transfer)))
                );
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceName(this.name());
    qbds.clearLinks();

    qbds.addLink(fieldNum(PlanActivityRelationship, IndependentPlanActivity), fieldNum(PlanActivity, RecId));

    qbds.addRange(fieldNum(PlanActivity, PlanActivityType)).value(
                    strFmt("@SYS26868",
                            SysQuery::value(enum2int(PlanActivityType::Process)),
                            SysQuery::value(enum2int(PlanActivityType::Transfer)))
                );
}

public void delete()
{
    ttsbegin;
    leanProdFlowPlanActivityRelationPred.delete();
    ttscommit;
    planActivityRelationshipPredecessor_DS.research();
}

public void init()
{
    super();

    predecessorActivityRange = this.query().dataSourceName(this.name()).addRange(fieldNum(PlanActivityRelationship, DependentPlanActivity));
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(LeanProdFlowPlanActivityRelation));
    successorProdFlowRelationRange = qbds.addRange(fieldNum(LeanProdFlowPlanActivityRelation, ProductionFlow));

    qbdsLeanProductionFlow = qbds.addDataSource(tableNum(LeanProductionFlow));
    qbdsLeanProductionFlow.clearLinks();
    qbdsLeanProductionFlow.addLink(fieldNum(LeanProdFlowPlanActivityRelation, ProductionFlow), fieldNum(LeanProductionFlow, RecId));
    successorLeanProductionFlowReference = qbdsLeanProductionFlow.addRange(fieldNum(LeanProductionFlow, ProductionFlowReference));
}

public void init()
{
    super();

    QueryBuildDataSource    qbds = this.query().dataSourceTable(tableNum(LeanProdFlowPlanActivityRelation));
    QueryBuildRange         qbr = qbds.addRange(fieldNum(LeanProdFlowPlanActivityRelation, ProductionFlow));
    qbr.value(queryValue(LeanProductionFlow::findPlan(callerPlan.RecId).RecId));
}

