public void clicked()
{
    MenuFunction menuFunction;
    Args args = new Args();

    menuFunction = new MenuFunction(menuitemDisplayStr(PlanActivityWizard), MenuItemType::Display);
    args.record(activePlan);
    menuFunction.run(args);

    planActivity_ds.executeQuery();
}
