[Form]
public class PlanActivityAddExisting extends FormRun
{
    Plan    callerPlan;

}

public void closeOk()
{
    PlanPlanActivity    planPlanActivity;
    PlanActivity        planActivity;

    planActivity = PlanActivity::findPlanActivityId(existingActivityId.text());

    if (planActivity)
    {
        if (!PlanPlanActivity::findPlanActivity(callerPlan.RecId,planActivity.RecId))
        {
            ttsbegin;
            planPlanActivity.Plan           = callerPlan.RecId;
            planPlanActivity.PlanActivity   = planActivity.RecId;
            planPlanActivity.insert();
            ttscommit;

            if (element.args().caller().name() == formStr(PlanActivity))
            {
                element.args().caller().refreshForm(planActivity);
            }
        }
        else
        {
            throw error("@SYS339393");
        }
    }
    else
    {
        throw error("@SYS302315");
    }

    super();
}

public void init()
{
    if (element.args().dataset() == tableNum(Plan))
    {
        callerPlan = element.args().record();
    }
    else
    {
        // Form was called incorrectly.
        throw error("@SYS22996");
    }

    super();
}

public void lookup()
{
    Args args = new Args();
    args.name(formstr(PlanActivityAddExistingLookup));
    args.caller(element);
    args.record(callerPlan);

    FormRun formRun = classfactory.formRunClass(args);
    formRun.init();
    this.performFormLookup(formRun);
}
