public class PlanActivityDetails extends FormRun implements PlanIPlanForm
{
    QueryBuildRange                 qbdrPlanActivityRecId;
    QueryBuildRange                 qbdrPlanActivityReceiptRecId;
    QueryBuildRange                 qbdrPlanActivityPickRecId;

    Plan                            plan;
    PlanActivity                    callerPlanActivity;
    CompanyInfo                     companyInfo;

    InventDimCtrl_Frm               inventDimForm_Ctrl;

}

public void enableFields()
{
    boolean usedInActivePlan          = planActivity.isUsedInActivePlan();
    boolean usedInIndependentRelation = planActivity.isUsedInIndependentRelation();
    boolean usedInDependentRelation   = planActivity.isUsedInDependentRelation();

    switch (planActivity.PlanActivityType)
    {
        case PlanActivityType::Process:
            wrkCtrResourceGroup_ds.object(fieldNum(WrkCtrResourceGroup,WrkCtrId)).allowEdit(!usedInActivePlan);
            planActivityReceipt_ds.object(fieldNum(PlanActivity,OnHandUpdate)).allowEdit(   !usedInIndependentRelation
                                                                                         && !leanProductionFlowActivity.ReceiveSemiFinished);
            leanProductionFlowActivity_ds.object(fieldNum(LeanProductionFlowActivity,PickUpSemiFinished)).allowEdit(!usedInDependentRelation);
            leanProductionFlowActivity_ds.object(fieldNum(LeanProductionFlowActivity,ReceiveSemiFinished)).allowEdit(   !usedInDependentRelation
                                                                                                                     && !planActivityReceipt.OnHandUpdate);
            PlanActivity_AutoCompleteKanban.value(NoYes::No);
            PlanActivity_AutoCompleteKanban.allowEdit(false);
            fromLocation.allowEdit(false);
            toLocation.allowEdit(false);
            break;
        case PlanActivityType::Transfer:
            wrkCtrResourceGroup_ds.object(fieldNum(WrkCtrResourceGroup,WrkCtrId)).allowEdit(false);
            planActivityReceipt_ds.object(fieldNum(PlanActivity,OnHandUpdate)).allowEdit(   !usedInIndependentRelation
                                                                                         && !leanProductionFlowActivity.ReceiveSemiFinished);
            planActivityPick_ds.object(fieldNum(PlanActivity,OnHandUpdate)).allowEdit(   !usedInDependentRelation
                                                                                      && !leanProductionFlowActivity.PickUpSemiFinished);
            leanProductionFlowActivity_ds.object(fieldNum(LeanProductionFlowActivity,PickUpSemiFinished)).allowEdit(   !usedInDependentRelation
                                                                                                                    && !usedInIndependentRelation
                                                                                                                    && !planActivityPick.OnHandUpdate
                                                                                                                    && !planActivityReceipt.OnHandUpdate);
            leanProductionFlowActivity_ds.object(fieldNum(LeanProductionFlowActivity,ReceiveSemiFinished)).allowEdit(   !usedInDependentRelation
                                                                                                                     && !usedInIndependentRelation
                                                                                                                     && !planActivityPick.OnHandUpdate
                                                                                                                     && !planActivityReceipt.OnHandUpdate);
            PlanActivity_AutoCompleteKanban.allowEdit(true);
            fromLocation.allowEdit(!usedInActivePlan);
            toLocation.allowEdit(!usedInActivePlan);
            break;
    }

    planActivity_ds.object(fieldNum(PlanActivity,FreightedBy)).allowEdit(!planActivity.areServicesAssociated());
}

public void init()
{
    Object caller;

    if (   !this.args()
        || !this.args().record()
        || this.args().dataset() != tableNum(PlanActivity))
    {
        throw error(Error::missingFormActiveBuffer(this.name()));
    }

    caller             = element.args().caller();
    callerPlanActivity = this.args().record();
    companyInfo        = CompanyInfo::find();

    super();
    this.setFormControls();
    this.updateDesign(InventDimFormDesignUpdate::Init);

    if (element.args().caller() is PlanIPlanForm)
    {
        PlanIPlanForm planForm = element.args().caller();
        plan = planForm.plan();
    }
}

/// <summary>
///    Initializes query for pick and receive activities.
/// </summary>
/// <param name="_qbds">
///    A <c>QueryBuildDataSource</c> object.
/// </param>
/// <param name="_planActivityType">
///    A <c>PlanActivityType</c> enum value.
/// </param>
public void initPlanActivityQuery(
    QueryBuildDataSource _qbds,
    PlanActivityType     _planActivityType)
{
    QueryBuildDataSource        qbdsPlanActivityRelationship;
    QueryBuildDataSource        qbdsPlanActivityConstraint;
    QueryBuildDataSource        qbdsIndependentPlanActivity;

    if (_qbds)
    {
        // the dependent pick or receive activity
        _qbds.clearLinks();
        _qbds.clearDynalinks();
        _qbds.addRange(fieldNum(PlanActivity,PlanActivityType)).value(SysQuery::value(_planActivityType));

        // the relation to the independent top level activity
        qbdsPlanActivityRelationship = _qbds.addDataSource(tableNum(PlanActivityRelationship));
        qbdsPlanActivityRelationship.fetchMode(QueryFetchMode::One2One);
        qbdsPlanActivityRelationship.joinMode(JoinMode::ExistsJoin);
        qbdsPlanActivityRelationship.addLink(fieldNum(PlanActivity, RecId),
                                             fieldNum(PlanActivityRelationship, DependentPlanActivity));

        // aggregation constraint
        qbdsPlanActivityConstraint = qbdsPlanActivityRelationship.addDataSource(tableNum(PlanConstraint));
        qbdsPlanActivityConstraint.fetchMode(QueryFetchMode::One2One);
        qbdsPlanActivityConstraint.joinMode(JoinMode::ExistsJoin);
        qbdsPlanActivityConstraint.addLink(fieldNum(PlanActivityRelationship, Constraint),
                                           fieldNum(PlanConstraint, RecId));
        qbdsPlanActivityConstraint.addRange(fieldNum(PlanConstraint, ConstraintType)).value(SysQuery::value(ConstraintTypes::Aggregation));

        // the independent top level activity
        qbdsIndependentPlanActivity = qbdsPlanActivityRelationship.addDataSource(tableNum(PlanActivity));
        qbdsIndependentPlanActivity.fetchMode(QueryFetchMode::One2One);
        qbdsIndependentPlanActivity.joinMode(JoinMode::ExistsJoin);
        qbdsIndependentPlanActivity.addLink(fieldNum(PlanActivityRelationship, IndependentPlanActivity),
                                            fieldNum(PlanActivity, RecId));

        switch (_planActivityType)
        {
            case PlanActivityType::Pick:
                qbdrPlanActivityPickRecId    = qbdsIndependentPlanActivity.addRange(fieldNum(PlanActivity, RecId));
                break;
            case PlanActivityType::Receive:
                qbdrPlanActivityReceiptRecId = qbdsIndependentPlanActivity.addRange(fieldNum(PlanActivity, RecId));
                break;
        }
    }
}

/// <summary>
///    Initializes the activity time query.
/// </summary>
/// <param name="_qbds">
///    A <c>QueryBuildDataSource</c> object.
/// </param>
public void initPlanActivityTimeQuery(
    QueryBuildDataSource _qbds)
{
    PlanActivityTime    planActivityTimeLocal;
    Map                 mapPlanActivityTimes;
    MapEnumerator       me;

    if (_qbds)
    {
        _qbds.clearLinks();
        _qbds.clearDynalinks();
        _qbds.clearRange(fieldNum(PlanActivityTime,RecId));

        mapPlanActivityTimes = planActivity.mapPlanActivityTimes();

        if (   mapPlanActivityTimes
            && mapPlanActivityTimes.elements())
        {
            me = mapPlanActivityTimes.getEnumerator();

            while (me.moveNext())
            {
                planActivityTimeLocal = me.currentValue();
                _qbds.addRange(fieldNum(PlanActivityTime,RecId)).value(queryValue(planActivityTimeLocal.RecId));
            }
        }
        else
        {
            _qbds.addRange(fieldNum(PlanActivityTime,RecId)).value(queryValue(0));
        }
    }
}

