/// <summary>
///    Triggers design updates to create or update the <c>InventDimForm</c> helpers.
/// </summary>
/// <param name="_mode">
///    An <c>InventDimFormDesignUpdate</c> value specifying what type of action this is; for example,
///    initialize, or update.
/// </param>
/// <exception cref="Exception::Error">
///    An invalid parameter was passed.
/// </exception>
public void updateDesign(InventDimFormDesignUpdate _mode = InventDimFormDesignUpdate::Active)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimForm_Ctrl)
            {
                inventDimForm_Ctrl = InventDimCtrl::newFromFormValidDataSource(element, inventDim_ds.name());
                inventDimForm_Ctrl.parmDimParmVisibleGrid(PlanActivityForm::inventDimParmShow());
            }
        // fall through
        case InventDimFormDesignUpdate::Active:
            inventDimForm_Ctrl.parmDimParmEnabled(PlanActivityForm::inventDimParmEnabled(leanProdFlowActivityPickingLocation.ItemId));
            inventDimForm_Ctrl.formSetControls(true);
            break;
        case InventDimFormDesignUpdate::FieldChange:
            PlanActivityForm::updateInventDimOnFieldChange(inventDim, leanProdFlowActivityPickingLocation.ItemId);
            inventDimForm_Ctrl.parmDimParmEnabled(PlanActivityForm::inventDimParmEnabled(leanProdFlowActivityPickingLocation.ItemId));
            inventDimForm_Ctrl.formSetControls(true);
            break;
        default:
            throw error(strFmt("@SYS54195", funcName()));
    }

    this.updateFormMode();
}

