public int active()
{
    int result = super();

    if (element.isCurrentLegalEntity())
    {
        leanProdFlowActivityPickingLocation.editOnHandUpdate(false, NoYes::No);
        leanProdFlowActivityPickingLocation.editRegisterScrap(false, NoYes::No);
    }
