[Form]
public class PlanActivityServiceDetails extends FormRun implements PlanIPlanActivityServiceForm
{
    QueryBuildRange             rangePlanActivityService;
    InventDimId                 serviceItemInventDimId;

    InventDimCtrl_Frm           inventDimForm_Ctrl_ServiceItem;
    InventDimCtrl_Frm           inventDimForm_Ctrl_OutputItem;

    AgreementLine               agreementLine;
    Plan                        plan;

}

public void init()
{
    PlanActivityService callerPlanActivityService;
    Object              caller;

    super();

    caller = element.args().caller();

    this.updateDesign(InventDimFormDesignUpdate::Init);

    callerPlanActivityService = element.args().record();

    rangePlanActivityService.value(queryValue(callerPlanActivityService.RecId));

    if (caller && caller is PlanIPlanForm)
    {
        PlanIPlanForm planForm = caller as PlanIPlanForm;
        plan = planForm.plan();
    }

    purchaseAgreementId.text(PurchAgreementHeader::find(callerPlanActivityService.PurchAgreementHeader).PurchNumberSequence);
}

public InventDimCtrl_Frm inventDimSetupObject()
{
    InventDimCtrl_Frm   inventDimCtrl_Frm;

    switch (this.selectedControl().name())
    {
        case productDimensions_ConfigId.name(),
             productDimensions_InventSizeId.name(),
             productDimensions_InventColorId.name(),
             productDimensions_InventStyleId.name(),
             productDimensions_InventVersionId.name():

            inventDimCtrl_Frm = inventDimForm_Ctrl_ServiceItem;
            break;

        default:
            inventDimCtrl_Frm = inventDimForm_Ctrl_OutputItem;
    }

    return inventDimCtrl_Frm;
}

public void setAgreement(PurchAgreementId _purchAgreementId)
{
    planActivityService.PurchAgreementHeader = PurchAgreementHeader::findAgreementId(_purchAgreementId).RecId;

    purchaseAgreementId.text(_purchAgreementId);
}

public void setAgreementLine(AgreementLine _agreementLine)
{
    UnitOfMeasureSymbol unitOfMeasureItem;

    agreementLine = _agreementLine;

    // Get unit of measure from the agreement line
    unitOfMeasureItem = AgreementLineQuantityCommitment::find(agreementLine.RecId).ProductUnitOfMeasure;

    // If no unit of measure is specified on the agreement line get the unit from the item
    if (!unitOfMeasureItem)
    {
        unitOfMeasureItem = InventTableModule::find(agreementLine.ItemId, ModuleInventPurchSales::Purch).UnitId;
    }

    // Use the found unit as the default service unit
    planActivityService.ServiceUnitOfMeasure = UnitOfMeasure::findBySymbol(unitOfMeasureItem).RecId;
}

public void setServiceItemInventDimId(InventDimId _inventDimId)
{
    serviceItemInventDimId = _inventDimId;
}

public Plan plan()
{
    return plan;
}

public void setTitleText()
{
    PlanActivity planActivity = PlanActivity::find(planActivityService.PlanActivity);

    headerTitle.text(strFmt('%1 : %2 - %3', planActivity.PlanActivityId,
                                            planActivity.Name,
                                            planActivityService.ItemId));
}

/// <summary>
///    Triggers design updates to create or update the <c>InventDimForm</c> helpers.
/// </summary>
/// <param name="_mode">
///    An <c>InventDimFormDesignUpdate</c> value specifying what type of action this is; for example,
///    initialize, or update.
/// </param>
/// <exception cref="Exception::Error">
///    An invalid parameter was passed.
/// </exception>
public void updateDesign(InventDimFormDesignUpdate _mode = InventDimFormDesignUpdate::Active)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimForm_Ctrl_ServiceItem)
            {
                inventDimForm_Ctrl_ServiceItem = InventDimCtrl::newFromFormValidDataSource(element, inventDimPlanActivityServiceItem_ds.name());
                inventDimForm_Ctrl_ServiceItem.parmDimParmVisibleGrid(PlanActivityForm::inventDimParmShow());
            }
        // fall through
        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::FieldChange:
            PlanActivityForm::updateInventDimOnFieldChange(inventDimPlanActivityServiceItem, planActivityService.ItemId);
            inventDimForm_Ctrl_ServiceItem.parmDimParmEnabled(PlanActivityForm::inventDimParmEnabled(planActivityService.ItemId));
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

/// <summary>
///    Triggers design updates to create or update the <c>InventDimForm</c> helpers used for output item.
/// </summary>
/// <param name="_mode">
///    An <c>InventDimFormDesignUpdate</c> value specifying what type of action this is; for example,
///    initialize, or update.
/// </param>
/// <exception cref="Exception::Error">
///    An invalid parameter was passed.
/// </exception>
public void updateDesignOutputItem(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimForm_Ctrl_OutputItem)
            {
                inventDimForm_Ctrl_OutputItem = InventDimCtrl::newFromFormValidDataSource(element, inventDimPlanActivityServiceOutputItem_ds.name());
                inventDimForm_Ctrl_OutputItem.parmDimParmVisibleGrid(PlanActivityForm::inventDimParmShowServiceOutput());
                inventDimForm_Ctrl_OutputItem.parmDimParmLockedRightClick(PlanActivityForm::inventDimParmLockedServiceOutput());
            }
        // fall through
        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::FieldChange:
            if (_mode == InventDimFormDesignUpdate::FieldChange)
            {
                PlanActivityForm::updateInventDimOnFieldChange(inventDimPlanActivityServiceOutputItem, planActivityServiceOutputItem.ItemId);
            }
            inventDimForm_Ctrl_OutputItem.parmDimParmEnabled(PlanActivityForm::inventDimParmEnabled(planActivityServiceOutputItem.ItemId));
            inventDimForm_Ctrl_OutputItem.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

