public int active()
{
    int ret;

    ret = super();

    PlanActivityService_CarrierCode.visible(planActivityService.CarrierCode != '');

    outputProductsTab.visible(!planActivityService.IsDefault);

    if (planActivityService.UseOutputConfiguration)
    {
        productDimensions_ConfigId.allowEdit(false);
    }

    if (planActivityService.UseOutputColor)
    {
        productDimensions_InventColorId.allowEdit(false);
    }

    if (planActivityService.UseOutputStyle)
    {
        productDimensions_InventStyleId.allowEdit(false);
    }

    if (planActivityService.UseOutputVersion)
    {
        productDimensions_InventVersionId.allowEdit(false);
    }

    if (planActivityService.UseOutputSize)
    {
        productDimensions_InventSizeId.allowEdit(false);
    }

    element.setTitleText();

    return ret;
}

