public void write()
{
    InventDim   inventDim;

    inventDim = InventDim::findOrCreate(inventDimPlanActivityServiceOutputItem);

    planActivityServiceOutputItem.InventDimId = inventDim.InventDimId;

    if (planActivityServiceOutputItem.InventDimId != inventDimPlanActivityServiceOutputItem.InventDimId)
    {
        inventDimPlanActivityServiceOutputItem.data(inventDim);
        inventDimPlanActivityServiceOutputItem_ds.setCurrent();
    }

    super();
}

