public boolean modified()
{
    boolean ret;

    ret = super();

    // Clear item selection as it might not be valid anymore
    planActivityService.ItemId = '';
    planActivityService.PurchAgreementHeader = 0;

    if (PlanActivityService::validateCarrierCode(this.text()))
    {
        serviceVendor.text(PlanActivityService::vendorAccountCarrierCompany(this.text()));

        if (serviceVendor.text() == '')
        {
            // Only carriers from companies with a vendor account can be used for the service activity
            ret = checkFailed("@SYS332097");

            planActivityService.CarrierCode = '';
        }
    }
    else
    {
        // Incorrect carrier code specified
        ret = checkFailed("@SYS332098");
    }

    planActivityService_ds.refresh();

    planActivityService_ServiceItemId.allowEdit(ret);

    return ret;
}

public void lookup()
{
    Args    args = new Args();
    FormRun formRun;

    args.name(formStr(PlanActivityServiceItemLookup));
    args.caller(element);

    formRun = classfactory.formRunClass(args) as FormRun;
    formRun.init();

    this.performFormLookup(formRun);
}

public boolean modified()
{
    AgreementLine   selectedAgreementLine;
    boolean         ret;

    ret = super();

    planActivityService.ServiceItemInventDimId = serviceItemInventDimId;

    inventDimPlanActivityServiceItem.data(InventDim::find(serviceItemInventDimId));
    inventDimPlanActivityServiceItem_ds.refresh();

    // Was item not selected through the lookup
    if (this.text() != agreementLine.ItemId)
    {
        selectedAgreementLine = PlanActivityService::findMatchingAgreementLine(planActivityService.vendorAccount(),
                                                                               this.text(),
                                                                               curext(),
                                                                               plan);

        if (selectedAgreementLine)
        {
            element.setAgreement(PurchAgreementHeader::find(selectedAgreementLine.Agreement).PurchNumberSequence);
            element.setAgreementLine(selectedAgreementLine);
            element.setServiceItemInventDimId(selectedAgreementLine.InventDimId);

            planActivityService.ServiceItemInventDimId = selectedAgreementLine.InventDimId;
            planActivityService_ds.refresh();
            inventDimPlanActivityServiceItem_ds.refresh();
        }
        else
        {
            // Item %1 is not found in any active purchase agreement for vendor %2
            error(strFmt("@SYS339395", this.text(), element.vendorAccount()));
            element.setAgreement('');
            element.setAgreementLine(selectedAgreementLine);
            element.setServiceItemInventDimId('');
            planActivityService.ItemId = '';
            planActivityService.ServiceUnitOfMeasure = 0;
            planActivityService_ds.refresh();
        }
    }

    return ret;
}

