public boolean modified()
{
    boolean ret;

    ret = super();

    if (    planActivityService.ServiceQuantityCalculationBase == PlanActivityServiceQuantityBase::ActivityTime
        && !UnitOfMeasure::find(planActivityService.ServiceUnitOfMeasure).belongsToUnitOfMeasureClass(UnitOfMeasureClass::Time))
    {
        planActivityService.ServiceUnitOfMeasure = 0;
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (this.selection() == PlanActivityServiceQuantityBase::ActivityTime && !UnitOfMeasure::find(planActivityService.ServiceUnitOfMeasure).belongsToUnitOfMeasureClass(UnitOfMeasureClass::Time, true))
    {
        // The option, Activity time, can only be used with the service unit class Time. Select the service unit class, Time, to use with Activity time, or select a different service quantity base.
        ret = checkFailed("@SYS332401");
    }

    return ret;
}
