Method.Source:
    [Form]
    /// <summary>
    /// This form shows the bulk/pack on-hand
    /// </summary>
    public class PmfBulkPackOnHand extends FormRun
    {
        PmfBulkPackOnHand               bulkPackOnHand;
        InventDimCtrl_Frm_PackOnHand    inventDimPackOnHand;
        InventDimCtrl_Frm_PackOnHand    inventDimBulkOnHand;
        InventDimCtrl_Frm               inventDimCtrl;
}

    Object bulkInventDimSetupObject()
    {
        InventDimParm   inventDimParm;
        Set             validDataSources;

        if (!InventDimBulkOnHand)
        {
            // Limit the control of InventDim type data sources to just BulkInventDim
            validDataSources = new Set(Types::String);
            validDataSources.add(BulkInventDim_ds.name());

            inventDimBulkOnHand = InventDimCtrl_Frm_PackOnHand::newFromForm(element);
			//Superclass has been initialized above and mark variable 'lastValueRead' with value true, 
			//need to reset to make sure subclass initialized correct.
            inventDimBulkOnHand.resetLastValueRead();
            inventDimBulkOnHand.parmValidDataSources(validDataSources);
            inventDimBulkOnHand.init();
            inventDimBulkOnHand.parmLargeRightClickForm(NoYes::Yes);

            // The following inventory dimensions will be disabled in setup.
            inventDimParm = inventDimBulkOnHand.parmDimParmLockedRightClick();
            inventDimParm.ItemIdFlag            = true;
            inventDimParm.InventSerialIdFlag    = true;
            inventDimParm.WMSLocationIdFlag     = true;
            inventDimParm.InventBatchIdFlag     = true;
            inventDimParm.InventLocationIdFlag  = true;
            inventDimParm.InventSiteIdFlag      = true;

            // The following inventory dimensions will be selected on the grid causing a checkmark to appear in dimension setup
            inventDimParm = inventDimBulkOnHand.parmDimParmVisibleGrid();
            inventDimParm.ItemIdFlag = true;

            // The following inventory dimensions will be visible in dimension setup
            inventDimParm = inventDimBulkOnHand.parmDimParmVisible();
            inventDimParm.ItemIdFlag = true;
        }

        return inventDimBulkOnHand;
    }

/// <summary>
/// This method intializes the form
/// </summary>
public void init()
{
    InventTable             inventTable;
    InventSum               inventSum;
    ProdTable               prodTable;
    PurchLine               purchLine;
    SalesLine               salesLine;
    TmpPmfConsolidatedBulk  tmpConsBulk;
    TmpPmfConsolidatedPack  tmpConsPack;
    RecId                   bomVersionRecId;

    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (!element.args().record())
    {
        throw error("@SYS22539");
    }

    switch (element.args().record().TableId)
    {
        case tablenum(InventTable):
            inventTable = element.args().record();
            break;
        case tablenum(InventSum):
            inventSum = element.args().record();
            inventTable = inventSum.inventTable();
            break;
        case tablenum(ProdTable):
            prodTable = element.args().record();
            inventTable = prodTable.inventTable();
            bomVersionRecId = prodTable.bomVersion().RecId;
            break;
        case tablenum(PurchLine):
            purchLine = element.args().record();
            inventTable = purchLine.inventTable();
            break;
        case tablenum(SalesLine):
            salesLine = element.args().record();
            inventTable = salesLine.inventTable();
            break;
        case tablenum(TmpPmfConsolidatedBulk):
            tmpConsBulk = element.args().record();
            inventTable = InventTable::find(tmpConsBulk.ItemId);
            break;
        case tablenum(TmpPmfConsolidatedPack):
            tmpConsPack = element.args().record();
            inventTable = InventTable::find(tmpConsPack.ItemId);
            break;
    }

    bulkPackOnHand = PmfBulkPackOnHand::construct(inventTable.ItemId);
    bulkPackOnHand.parmBOMVersionRecId(bomVersionRecId);
    bulkPackOnHand.getBulkItems();
    tmpBulkItems.setTmpData(bulkPackOnHand.parmTmpBulkItems());
    tmpBulkItems_DS.research();
}

Object packInventDimSetupObject()
{
    InventDimParm   inventDimParm;
    Set             validDataSources;

    if (!inventDimPackOnHand)
    {
        // Limit the control of InventDim type data sources to just InventDim
        validDataSources = new Set(Types::String);
        validDataSources.add(InventDim_ds.name());

        inventDimPackOnHand = InventDimCtrl_Frm_PackOnHand::newFromForm(element);
        //Superclass has been initialized above and mark variable 'lastValueRead' with value true,
        //need to reset to make sure subclass initialized correct.
        inventDimPackOnHand.resetLastValueRead();
        inventDimPackOnHand.parmValidDataSources(validDataSources);
        inventDimPackOnHand.init();

        // The following inventory dimensions will be disabled in setup.
        inventDimParm = inventDimPackOnHand.parmDimParmLockedRightClick();
        inventDimParm.ItemIdFlag = true;

        // The following inventory dimensions will be visible in dimension setup
        inventDimParm = inventDimPackOnHand.parmDimParmVisible();
        inventDimParm.ItemIdFlag = true;
    }

    return inventDimPackOnHand;
}

void refreshTmpData()
{
    QueryBuildDataSource qbds;
    InventDimParm inventDimParm = element.packInventDimSetupObject().parmDimParmVisibleGrid();

    bulkPackOnHand.parmInventDimParm(inventDimParm);
    bulkPackOnHand.getPackedItems(tmpBulkItems.BulkItemId);
    tmpPackItems.setTmpData(bulkPackOnHand.parmTmpPackItems());

    //Need this for Count history form to work correctly
    qbds = inventDim_ds.query().dataSourceTable(tablenum(InventDim));
    inventDimParm = element.packInventDimSetupObject().parmDimParmVisibleGrid();
    inventDimParm.queryAddSortField(qbds);

    //refetch the data with the new dimension setup
    tmpPackItems_ds.executeQuery();
}

/// <summary>
/// This method is needed by the InventDimCtrl_FrmPackOnHand to be able to refresh the
/// data correctly with temporary tables versus standard datasources.
/// </summary>
void rightClickModifyQuery()
{
    if (inventDimCtrl != inventDimBulkOnHand)
    {
        element.refreshTmpData();
    }
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init          :
            //Fall Through
        case InventDimFormDesignUpdate::LinkActive,
             InventDimFormDesignUpdate::FieldChange   :
            element.packInventDimSetupObject().initFromCaller();

        case InventDimFormDesignUpdate::Active        :
            element.bulkInventDimSetupObject().formActiveSetup();
            element.bulkInventDimSetupObject().formSetControls(true,true);
            element.packInventDimSetupObject().formActiveSetup();
            element.packInventDimSetupObject().formSetControls(true,true);
            break;

        default :
            throw error(strfmt("@SYS54195",funcname()));
    }
}

public int active()
{
    int ret;

    ret = super();

    element.refreshTmpData();

    return ret;
}

/// <summary>
/// This method handles when the intercompany on-hand button is clicked
/// </summary>
public void clicked()
{
    MenuFunction            menuFunction;
    FormRun                 configForm;
    Args                    args;
    InventSum               inventSumLocal;

    select firstonly inventSumLocal
        where inventSumLocal.ItemId == tmpPackItems.ItemId;

    menuFunction = new MenuFunction(
                        menuitemdisplaystr(InventOnHandInterCompany),
                        MenuItemType::Display);

    args = new Args();
    args.caller(element);
    args.record(inventSumLocal);
    configForm = menuFunction.create(args);
    configForm.run();
}

void clicked()
{
    InventDimParm   inventDimParm;
    inventDimCtrl = element.packInventDimSetupObject();

    // The following inventory dimensions will be selected on the grid causing a checkmark to appear in dimension setup
    inventDimParm = inventDimPackOnHand.parmDimParmVisibleGrid();
    inventDimParm.ItemIdFlag = true;

    super();
}
