/// <summary>
/// This forms shows the bulk/pack orders that can be consolidated before firming
/// </summary>
public class PmfBulkPlanConsolidate extends FormRun
{
    PmfBulkPlanMarkValidate bulkPlanMarkValidate;
    InventDimCtrl_Frm       inventDimFormSetup;
    InventDimParm           inventDimParm;
    Map                     mapReqPoDeleted;
    FormObservableLink      bulkQtyObservable = new FormObservableLink();

}

public boolean canClose()
{
    boolean                     ret;
    TmpPmfConsolidatedOrder     orders;
    boolean                     orderCreated = PmfConsOrd::exist(TmpConsOrd.ConsOrdId);

    ret = super();

    if (ret)
    {
        orders.setTmpData(tmpConsOrd_ds.cursor());
        select firstonly TableId from orders
            where orders.ReqTransMark;

        if (orders)
        {
            if (Box::yesNo("@PRO2476",DialogButton::Yes)
                ==DialogButton::No)
            {
                ret = false;
            }
        }
    }

    if (ret)
    {
        if (!orderCreated)
        {
            bulkPlanMarkValidate.numberSeqConsOrderIdAbort();
        }
    }

    return ret;
}

/// <summary>
/// This method overrides a form method event when the form is about to close ok
/// </summary>
/// <returns>returns kernel value</returns>
public boolean closedOk()
{
    boolean ret;

    bulkPlanMarkValidate.parmMapReqPODeleted(element.parmMapReqPODeleted());

    ret = super();

    return ret;
}

/// <summary>
/// This firms the consolidated orders and refreshes the data
/// </summary>
public void createConsOrder()
{
    PmfBulkPlanFirm bulkPlanFirm;

    bulkPlanFirm = PmfBulkPlanFirm::construct();
    bulkPlanFirm.parmTmpConsOrd(tmpConsOrd_ds.cursor());
    bulkPlanFirm.parmTmpConsBulk(bulkPlanMarkValidate.parmTmpConsBulk());
    bulkPlanFirm.parmTmpConsPack(bulkPlanMarkValidate.parmTmpConsPack());
    bulkPlanFirm.parmMapReqPODeleted(element.parmMapReqPODeleted());
    bulkPlanFirm.run();
    element.parmMapReqPODeleted(bulkPlanFirm.parmMapReqPODeleted());
    tmpConsOrd.setTmpData(bulkPlanFirm.parmTmpConsOrd());
    tmpConsOrd_ds.research();
}

/// <summary>
/// This method intializes the form
/// </summary>
public void init()
{
    #define.Cons('Cons')
    #define.Bulk('Bulk')

    super();

    if (classidget(element.args().caller())
            != classnum(PmfBulkPlanMarkValidate))
    {
        throw error("@PRO2477");
    }

    inventDimParm.InventSiteIdFlag = true;
    inventDimParm.InventLocationIdFlag = true;
    element.updateDesign(InventDimFormDesignUpdate::Init);

    bulkPlanMarkValidate = element.args().caller();
    tmpConsOrd.setTmpData(bulkPlanMarkValidate.parmTmpConsOrd());
    tmpConsBulk.setTmpData(bulkPlanMarkValidate.parmTmpConsBulk());
    tmpConsPack.setTmpData(bulkPlanMarkValidate.parmTmpConsPack());
    tmpConsOrd_ds.research();
}

