/// <summary>
/// This method is called when the form needs to respond to different events
/// </summary>
/// <param name="_mode">mode for the form</param>
void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init          :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl::newFromForm(element);
            }
            inventDimFormSetup.parmDimParmEnabled(null);
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm);
            inventDimFormSetup.formSetControls(true,true);

            this.formSetControls();
            break;

        default:
            throw error(strfmt("@SYS54195",funcname()));
    }
}

