/// <summary>
/// This method is called when the datasource will run the query to get the data
/// </summary>
public void executeQuery()
{
    QueryBuildRange qbr;

    qbr = this.query()
            .dataSourceNo(1)
            .findRange(fieldnum(TmpPmfConsolidatedBulk, ConsOrdId));

    if (!qbr)
    {
        qbr = this.query()
                .dataSourceNo(1)
                .addRange(fieldnum(TmpPmfConsolidatedBulk, ConsOrdId));
    }

    qbr.value(queryValue(tmpConsOrd.ConsOrdId));
    super();
}

/// <summary>
/// This method returns allocated qty of the bulk
/// </summary>
/// <param name="_bulk">Consolidated bulk order</param>
/// <returns>Returns sum of the allocated</returns>
display PmfBulkAllocated sumAllocated(TmpPmfConsolidatedBulk _bulk)
{
    bulkQtyObservable.observe();

    Qty qty = this.getSumOfPackCovQty(_bulk);

    return qty;
}

/// <summary>
/// The method calculates and returns total of pack orders' extended quantity (coverage quantity).
/// </summary>
/// <param name = "_bulk">Consolidated bulk order</param>
/// <returns>Sum of pack orders coverage quantity.</returns>
protected Qty getSumOfPackCovQty(TmpPmfConsolidatedBulk _bulk)
{
    TmpPmfConsolidatedPack sumPack;
    TmpPmfConsolidatedBulk sumBulk;

    sumBulk.setTmpData(tmpConsBulk_ds.cursor());
    sumPack.setTmpData(tmpConsPack_ds.cursor());

    select firstOnly BOMUnitId, CovInventDimId, ItemId from sumBulk
        where sumBulk.ConsOrdId == _bulk.ConsOrdId;

    Qty qty = 0;

    while select sum(CovQty), ExtQtyUnitId
        from sumPack
        group by sumPack.ExtQtyUnitId
        where sumPack.ConsOrdId == _bulk.ConsOrdId
    {
        if (sumPack.ExtQtyUnitId != sumBulk.BOMUnitId)
        {
            qty += EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(sumBulk.ItemId,
                                                                                    sumBulk.CovInventDimId,
                                                                                    sumPack.CovQty,
                                                                                    sumPack.ExtQtyUnitId,
                                                                                    sumBulk.BOMUnitId,
                                                                                    NoYes::Yes,
                                                                                    NoYes::Yes);
        }
        else
        {
            qty += sumPack.CovQty;
        }
    }

    return qty;
}

/// <summary>
/// This method returns sum of bulk qty
/// </summary>
/// <param name="_bulk">Consolidated bulk order</param>
/// <returns>returns sum of bulk qty</returns>
display ReqQty sumReqQty(TmpPmfConsolidatedBulk _bulk)
{
    TmpPmfConsolidatedBulk sumBulk;

    bulkQtyObservable.observe();

    sumBulk.setTmpData(tmpConsBulk_ds.cursor());

    select sum(Qty)
        from sumBulk
        where sumBulk.ConsOrdId == _bulk.ConsOrdId;

    return sumBulk.Qty;
}

/// <summary>
/// This method returns sum of unallocated bulk qty
/// and displays a red background if negative
/// </summary>
/// <param name="_bulk">Consolidated bulk order</param>
/// <returns>returns sum of unallocated bulk qty</returns>
display PmfBulkUnallocated unallocatedQty(TmpPmfConsolidatedBulk _bulk)
{
    #COSNumbers

    bulkQtyObservable.observe();

    ReqQty unallocated = this.sumReqQty(_bulk) - this.sumAllocated(_bulk);

    if (!_bulk.Qty)
    {
        unallocated = 0;
    }

    if (unallocated < 0)
    {
        warning("@SCM:PmfBulkPlanConsolidateUpdatedQtyNotValidForCurrentFormula");
        unallocatedQty.colorScheme(FormColorScheme::RGB);
        unallocatedQty.backgroundColor(WinAPI::RGBCon2int([255, 0, 0]));
    }
    else
    {
        unallocatedQty.colorScheme(FormColorScheme::WindowsPalette);
        unallocatedQty.backgroundColor(WindowsPalette::DisabledText);
    }

    return unallocated;
}

            public void modified()
            {
                InventItemOrderSetupMap inventItemOrderSetupMap;
                BOMVersion              bomVersion;

                super();

                inventItemOrderSetupMap
                    = InventItemOrderSetupMap::find(
                                    tmpConsBulk.ItemId,
                                    InventItemOrderSetupType::Invent,
                                    tmpConsBulk.CovInventDimId);

                tmpConsBulk.Qty
                    = InventTable::calculateOrderQuantity(
									inventItemOrderSetupMap,
			   					    tmpConsBulk.CovInventDimId,
									tmpConsBulk.MakeQty,
									tmpConsBulk.bomUnitId);

                bomVersion
                    = BOMVersion::findApproved(
                                    tmpConsBulk.ItemId,
                                    tmpConsBulk.ItemBomId,
                                    true,
                                    tmpConsBulk.ReqDateOrder, tmpConsBulk.ReqDateOrder, tmpConsBulk.Qty);

                tmpConsBulk.Qty     = bomVersion.pmfSetMultipleQty(tmpConsBulk.Qty);
                tmpConsBulk.MakeQty = tmpConsBulk.Qty;
                tmpConsBulk.write();
                tmpConsBulk_ds.reread();

                tmpConsOrd.ConsQty = tmpConsBulk.MakeQty;
                tmpConsOrd.write();
                tmpConsOrd_ds.reread();
            }

