/// <summary>
/// This method handles the modified event when the make qty for the pack is modified
/// </summary>
public void modified()
{
    super();

    InventItemOrderSetupMap inventItemOrderSetupMap = InventItemOrderSetupMap::find(
                                                            tmpConsPack.ItemId,
                                                            InventItemOrderSetupType::Invent,
                                                            tmpConsPack.CovInventDimId );

    BOMVersion bomVersion = BOMVersion::findApproved(
                            tmpConsPack.ItemId,
                            tmpConsPack.ItemBomId,
                            true,
                            tmpConsPack.ReqDateOrder, tmpConsPack.ReqDateOrder, tmpConsPack.MakeQty);
    if (!bomVersion)
    {
        InventQty modifiedQty   = tmpConsPack.MakeQty;
        tmpConsPack.MakeQty     = tmpConsPack.orig().MakeQty;
        tmpConsPack_ds.write();
        tmpConsBulk_ds.refresh();
        throw error(strFmt(BOMVersion::txtNotExist(),
                                    tmpConsPack.ItemId,
                                    "@SYS328687" + date2StrUsr(tmpConsPack.ReqDateOrder, DateFlags::FormatAll),
                                    "@SYS300379" + strFmt("%1", modifiedQty)));
    }

    BOM bomBulkItem;
    select firstonly bomBulkItem
        where  bomBulkItem.bomId    == bomVersion.bomId
            && bomBulkItem.ItemId   == bomVersion.PmfBulkParent;

    tmpConsPack.Qty = InventTable::calculateOrderQuantity(
                            inventItemOrderSetupMap,
                            tmpConsPack.CovInventDimId,
                            tmpConsPack.MakeQty,
                            tmpConsPack.bomUnitId);

    tmpConsPack.Qty     = bomVersion.pmfSetMultipleQty(tmpConsPack.Qty);
    tmpConsPack.MakeQty = tmpConsPack.Qty;

    BOMCalcConsumption bomCalcConsumption = BOMCalcConsumption::construct(
                                                    BOMFormula::Formula0,
                                                    BOMCalcData::newInventTable(
                                                        tmpConsPack.MakeQty,
                                                        InventTable::find(tmpConsPack.ItemId)),
                                                    bomVersion.PmfYieldPct,
                                                    true);

    tmpConsPack.CovQty = bomCalcConsumption.calcConsumption(bomBulkItem);
    tmpConsPack.ExtQty = tmpConsPack.CovQty;
    tmpConsPack_ds.write();
    tmpConsPack_ds.reread();
    tmpConsBulk_ds.refresh();

    bulkQtyObservable.markChanged();
}

public void clicked()
{
    element.createConsOrder();
}

/// <summary>
/// This method handles when the pack on-hand button is clicked
/// </summary>
public void clicked()
{
    Args            parameters = new Args();
    MenuFunction    packOnHand;

    super();
    packOnHand = new MenuFunction(
                    menuitemdisplaystr(PmfPackOnHand),
                    MenuItemType::Display);

    parameters.caller(this);

    if (tmpConsBulk)
    {
        parameters.record(tmpConsBulk);
    }
    else
    {
        parameters.record(tmpConsPack);
    }

    packOnHand.run(parameters);
}
