Method.Source:
[Form]
public class PmfCoByProdCalc extends FormRun
{
    #resAppl
    #PmfNumbers
    ProdTable prodTable;
    CostSheetPanel_PmfCoByProdCalcTrans costSheetPanel;

    ProdId prodId;

}

public void init()
{
    costSheetPanel = CostSheetPanel_PmfCoByProdCalcTrans::newForm(element,
        element.controlId(formControlStr(PmfCoByProdCalc,CostSheetPanelTabPage)));

    super();

    if (element.args().dataset() != tablenum(ProdTable))
        throw error(strfmt("@SYS19096",element.name(),tablestr(ProdTable)));
    prodTable = element.args().record();

    costSheetPanel.init();

    this.setFormFilters();
}

private void setFormFilters()
{
    var qbd = PmfProdCoByProduct_DS.queryBuildDataSource();
    var qFilter1 = PmfProdCoByProduct_DS.query().addQueryFilter(qbd, fieldStr(PmfProdCoBy, ItemId));
    var qFilter2 = PmfProdCoByProduct_DS.query().addQueryFilter(qbd, fieldStr(PmfProdCoBy, ProdId));
    qFilter2.status(RangeStatus::Hidden);
}

public int active()
{
    int ret;

    ret = super();

    costSheetPanel.parmPmfCoBy(pmfProdCoByProduct);
    costSheetPanel.fill();

    PmfCoByProdCalcTrans_ds.research();
    this.enableButtons();

    return ret;
}

void enableButtons()
{
    if (pmfProdCoByProduct.inventTable().ModelGroup().inventModelType().stdCostBased()
        && pmfProdCoByProduct.prodTable().ProdStatus == ProdStatus::Completed)
    {
        btnPmfCoByProdCalcVarianceTrans.enabled(true);
    }
    else
    {
        btnPmfCoByProdCalcVarianceTrans.enabled(false);
    }
}

public void init()
{
    super();

    this.cacheAddMethod(tablemethodStr(PmfCoByProdCalcTrans, displayUnitId), false);

    QueryBuildDataSource qbds = this.query().dataSourceNo(1);

    qbds.addSortField(fieldNum(PmfCoByProdCalcTrans, TransRefId));
    qbds.addSortField(fieldNum(PmfCoByProdCalcTrans, TransRefType));
    qbds.addSortField(fieldNum(PmfCoByProdCalcTrans, LineNum));
}
