public void calcPegging()
{
    ReqTrans                tmpReqTrans, tmpReqTrans2;
    ReqTransCov             tmpReqCov;
    MapIterator             mi;
    ReqPO                   reqPoTmp;
    InventQty               covQty;
    RefRecId                planVersion;
    QueryBuildDataSource    qbds;

    qbds = reqTransPegging_ds.query().dataSourceTable(tableNum(ReqTrans));

    qbds.clearRanges();
    qbds.clearDynalinks();
    qbrSettled = SysQuery::findOrCreateRange(qbds, fieldNum(ReqTrans, RecId));

    btnAddPo.enabled(false);
    delete_from tmpReqTransSettled;

    if (mapReqPO)
    {
        element.createBOMExplodeMap();

        // Find the demand linked to selected planned orders
        mi = new MapIterator(mapReqPO);
        mi.begin();
        while (mi.more())
        {
            reqPoTmp = mapReqPO.lookup(mi.key());
            if (reqPoTmp.RecId)
            {
                while select PlanVersion, RecId
                    from tmpReqTrans
                    where tmpReqTrans.PlanVersion  == reqPoTmp.PlanVersion
                        && tmpReqTrans.RefType   == reqPoTmp.RefType
                        && tmpReqTrans.RefId     == reqPoTmp.RefId
                        && tmpReqTrans.Direction == InventDirection::Receipt
                    join IssueRecId, RecId
                        from tmpReqCov
                        where tmpReqCov.ReceiptRecId == tmpReqTrans.RecId
                    join tmpReqTrans2
                        where tmpReqTrans2.Direction == InventDirection::Issue
                            && tmpReqTrans2.RecId  == tmpReqCov.IssueRecId
                            && tmpReqTrans2.PlanVersion == tmpReqTrans.PlanVersion
                {
                    covQty = element.findBOMCov(tmpReqTrans2.ItemId, -tmpReqTrans2.Qty, tmpReqTrans2.inventUnitId());

                    if (covQty)
                    {
                        tmpReqTransSettled.clear();
                        tmpReqTransSettled.initFromReqTransIssue(tmpReqTrans2);
                        tmpReqTransSettled.CovQty               = covQty;
                        tmpReqTransSettled.SettledId            = tmpReqTrans2.RecId;
                        tmpReqTransSettled.ReqTransCovRecId     = tmpReqCov.RecId;
                        tmpReqTransSettled.insert();

                        qbrSettled.value(strFmt("@PRO355",qbrSettled.value(),SysQuery::value(tmpReqTrans2.RecId)));

                        planVersion = tmpReqTrans2.PlanVersion;
                    }
                }
            }
            mi.next();
        }

        if (cbShowCovered.value() == NoYes::No)
        {
            //Find demand not linked to the selected planned orders
            while select PlanVersion, RecId
                from tmpReqTrans
                where tmpReqTrans.PlanVersion == planVersion
                    && tmpReqTrans.Direction == InventDirection::Receipt
                    && (tmpReqTrans.RefType  == ReqRefType::ItemPlannedOrder
                        || tmpReqTrans.RefType == ReqRefType::BOMPlannedOrder
                        || tmpReqTrans.RefType == ReqRefType::PmfPlannedProdBatch)
                    && tmpReqTrans.RefId
                join IssueRecId
                    from tmpReqCov
                    where tmpReqCov.ReceiptRecId == tmpReqTrans.RecId
                join tmpReqTrans2
                    where tmpReqTrans2.Direction == InventDirection::Issue
                        && tmpReqTrans2.RecId  == tmpReqCov.IssueRecId
                        && tmpReqTrans2.PlanVersion == tmpReqTrans.PlanVersion
            {
                select firstonly tmpReqTransSettled
                    where tmpReqTransSettled.SettledId == tmpReqTrans2.RecId;

                if (!tmpReqTransSettled)
                {
                    covQty = element.findBOMCov(tmpReqTrans2.ItemId, -tmpReqTrans2.Qty, tmpReqTrans2.inventUnitId());

                    if (covQty)
                    {
                        tmpReqTransSettled.clear();
                        tmpReqTransSettled.initFromReqTransIssue(tmpReqTrans2);
                        tmpReqTransSettled.CovQty               = covQty;
                        tmpReqTransSettled.ReqTransCovRecId     = tmpReqCov.RecId;

                        qbrSettled.value(strFmt("@PRO355", qbrSettled.value(),SysQuery::value(tmpReqTrans2.RecId)));

                        tmpReqTransSettled.insert();
                    }
                }
            }
        }
    }

    reqTransPegging_ds.executeQuery();
}

public void calcQtyRequiredForPlannedItem()
{
    MapIterator             mi;
    ReqPO                   reqPoTmp;
    BOM                     bomTmp;
    Qty                     qty,tmpqty;
    Qty                     maxqty = 0;
    container               itemconsum;
    int                     nPos;

    PmfFormulaCoBy          coBy;
    UnitOfMeasureDecimalPrecision decimalPrecision;

    if (mapReqPO)
    {
        mi = new MapIterator(mapReqPO);
        mi.begin();
        while (mi.more())
        {
            reqPoTmp = mapReqPO.lookup(mi.key());
            if (reqPoTmp.RecId)
            {
                while select bomTmp
                    where bomTmp.bomId == formula.bomId
                        && bomTmp.ItemId == reqPoTmp.ItemId
                {
                    if (bomTmp.bomQty() == 0)
                    {
                        continue;
                    }
                    qty = reqPoTmp.Qty / bomTmp.bomQty();
                    nPos = conFind(itemconsum, bomTmp.ItemId);
                    if (nPos > 0)
                    {
                        tmpqty = conPeek(itemConsum,nPos+1);
                        qty += tmpqty;
                    }
                    if (qty > maxqty )
                    {
                        maxqty = qty;
                    }

                    if (nPos > 0)
                    {
                        itemConsum = conPoke(itemConsum,nPos+1,qty);
                    }
                    else
                    {
                        itemConsum = conIns(itemConsum,conLen(itemConsum)+1,bomTmp.ItemId, qty);
                    }
                }

                while select coBy
                    where coBy.bomId == formula.bomId
                        && coBy.ItemId == reqPoTmp.ItemId
                {
                    if (coBy.coByQty() == 0)
                    {
                        continue;
                    }
                    qty = reqPoTmp.Qty / coBy.coByQty();
                    nPos = conFind(itemconsum, coBy.ItemId);
                    if (nPos > 0)
                    {
                        tmpqty = conPeek(itemConsum,nPos+1);
                        qty += tmpqty;
                    }
                    if (qty > maxqty )
                    {
                        maxqty = qty;
                    }

                    if (nPos > 0)
                    {
                        itemConsum = conPoke(itemConsum,nPos+1,qty);
                    }
                    else
                    {
                        itemConsum = conIns(itemConsum,conLen(itemConsum)+1,coBy.ItemId, qty);
                    }
                }
            }
            mi.next();
        }
        decimalPrecision = UnitOfMeasure::findBySymbol(element.planningUnitId()).DecimalPrecision;
        planItemQty  = formula.pmfSetMultipleQty(decRound(maxqty,decimalPrecision),true);
        if (PdsGlobal::pdsIsCWItem(planningItemId.text()))
        {
            planItemQty = PdsCatchWeight::cwRoundUpInventQty(planningItemId.text(),planItemQty);
            pdsCWPlanItemQty = PdsCatchWeight::cwQty(planningItemId.text(),planItemQty);
        }
    }
    else
    {
        throw error("@PRO1002");
    }
}

public void createBOMExplodeMap()
{
    BOM             tmpBOM;
    PmfFormulaCoBy  coBy;

    mapExplode      = new Map(Types::Int64,Types::Container);
    mapCoExplode    = new Map(Types::Int64,Types::Container);

    while select tmpBOM
        where tmpBOM.bomId == formula.bomId
    {
        if (!mapExplode.exists(tmpBOM.RecId))
        {
            mapExplode.insert(tmpBOM.RecId,[tmpBOM.ItemId,tmpBOM.bomQty() * planItemQty,tmpBOM.UnitId]);
        }
    }

    while select coBy
        where coBy.ProductType        == PmfProductType::Co_Product
           && coBy.bomVersionRefRecId == formula.RecId
    {
        if (!mapCoExplode.exists(coBy.RecId))
        {
            mapCoExplode.insert(coBy.RecId, [coBy.ItemId, coBy.PmfCoByMap::coByQty() * planItemQty, coBy.inventUnitId(), coBy.InventDimId]);
        }
    }
}

public void createMasterPlan()
{
    PmfCoSumUp sumUp;

    sumUp = PmfCoSumUp::construct();
    sumUp.tmpReqTrans(tmpReqTransSettled);
    sumUp.parmPlanItem(planningItemId.text());
    sumUp.parmQty(planItemQty);

    if (PdsGlobal::pdsIsCWItem(planningItemId.text()))
    {
        sumUp.parmPdsCWQty(pdsCWPlanItemQty);
    }

    sumUp.parmMapReqPO(mapReqPO);
    sumUp.parmPlanBomId(formula.bomId);
    sumUp.parmSiteId(siteId.text());
    sumUp.run();
}

/// <summary>
/// Find quantity for the BOM Co-product explode map.
/// </summary>
/// <param name = "_itemId">The item being used in conversion.</param>
/// <param name = "_qty">Current quantity in map.</param>
/// <param name = "_unitId">The unit to convert the quantity too.</param>
/// <param name = "_inventDimId">The dimension ID for conversions.</param>
/// <returns>Total converted quantity.</returns>
public InventQty findBOMCov(
    ItemId              _itemId, 
    InventQty           _qty, 
    UnitOfMeasureSymbol _unitId)
{
    Qty covQty;

    MapEnumerator mapEnum = mapCoExplode.getEnumerator();
    while (mapEnum.moveNext())
    {
        Qty                     qty;
        ItemId                  itemId;
        InventDimId             dimId;
        UnitOfMeasureSymbol     fromUnitId;

        [itemId, qty, fromUnitId, dimId] = mapEnum.currentValue();

        if (itemId == _itemId && qty > 0)
        {
            qty = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(itemId,
                                                                                        dimId,
                                                                                        qty,
                                                                                        fromUnitId,
                                                                                        _unitId,
                                                                                        NoYes::Yes);
            if (qty >= _qty)
            {
                covQty += _qty;
                mapCoExplode.insert(mapEnum.currentKey(),[itemId, qty - _qty , fromUnitId, dimId]);
            }
            else
            {
                covQty += qty;
                mapCoExplode.insert(mapEnum.currentKey(),[itemId, 0 , fromUnitId, dimId]);
            }
        }
    }

    return covQty;
}

