public void setMap()
{
    Set                             planningFormulaItemIds;
    ReqPO                           tmpPO;
    ReqTrans                        tmpReqTrans;
    ReqTransPOMultiSelectionContext multiSelectionContext;

    mapReqPO = new Map(Types::Integer, Types::Record);
    planningFormulaItemIds = new Set(extendedTypeId2Type(extendedTypeNum(PmfPlanningItemId)));

    // Get and loop through the multi-selected selected ReqTrans records from the calling form
    multiSelectionContext   = ReqTransPOMultiSelectionContext::construct(element.args());

    tmpReqTrans = multiSelectionContext.getFirst();
    while (tmpReqTrans.RecId)
    {
        tmpPO       = tmpReqTrans.reqPo();
        planningFormulaItemIds.add(tmpPO.PmfPlanningItemId);

        if (tmpPO.RecId && !mapReqPO.exists(tmpPO.RecId))
        {
            mapReqPO.insert(tmpPO.RecId, tmpPO);
        }

        tmpReqTrans = multiSelectionContext.getNext();
    }

    // If no multi-selection were made then use the current selected ReqPO record
    if (mapReqPO.elements() == 0 && element.args().dataset() == tableNum(ReqPO) && element.args().record())
    {
        tmpPO       = element.args().record();
        planningFormulaItemIds.add(tmpPO.PmfPlanningItemId);

        mapReqPO.insert(tmpPO.RecId, tmpPO);
    }

    //
    // if there is only one selected Planned Batch order,
    // then speed up load time of the form by pre-selecting it
    //
    if (tmpPO.PmfPlanningItemId && planningFormulaItemIds.elements() == 1)
    {
        formula_DS.autoSearch(true);
        this.setPlanningItem(tmpPO.PmfPlanningItemId);
        planningItemId.enabled(true);
    }
}

