public void initializeBOMForItem(PmfPlanningItemId _itemId)
{
    BOMVersion bomVersion;

    select firstonly bomVersion
        order by ItemId, Active, FromQty desc
        where bomVersion.ItemId == _itemId &&
              bomVersion.Active == true;

    BOMId.text(bomVersion.BOMId);
    this.bomModified();
}

