    formulaExplode_ds.executeQuery();
    pmfFormulaCoByProduct_ds.executeQuery();
}

public void addOrder()
{
    ReqPO       tmpPO;
    ReqTrans    reqTransReceipt;
    ReqTransCov reqTransCov;

    select forupdate ReqTransCovRecId from tmpReqTransSettled
            where tmpReqTransSettled.ReqTransRefRecId == ReqTransPegging.RecId;

    select reqTransReceipt
            exists join reqTransCov
            where reqTransCov.RecId == tmpReqTransSettled.ReqTransCovRecId
              &&  reqTransReceipt.RecId == reqTransCov.ReceiptRecId;

    tmpPO = reqTransReceipt.reqPo();

    if (!mapReqPO.exists(tmpPO.RecId))
    {
        mapReqPO.insert(tmpPO.RecId, tmpPO);
        reqPO_ds.executeQuery();
        this.calcPegging();
    }
}

