public void executeQuery()
{
    PmfPlanningItemId selectedItemId = planningItemId.text();
    BOMId             selectedBOMId  = BOMId.text();

    if ((selectedItemId != selectedFormulaPlanningItemId) || (selectedBOMId != selectedFormulaBOMId))
    {
        selectedFormulaPlanningItemId = selectedItemId;
        this.setupQueryRange(qbrPlanningItem, selectedFormulaPlanningItemId);

        selectedFormulaBOMId = selectedBOMId;
        this.setupQueryRange(qbrBOMId, selectedFormulaBOMId);

        qbrFormulaApproved.value(enum2str(NoYes::Yes));

        super();

        element.calcQtyRequiredForPlannedItem();
        element.calcPegging();

        pmfFormCtrl_PmfCoReqPlanning.updateDesign();
    }
}

