public display PmfExplodeQty explodeQty(BOM _bom)
{
    PmfExplodeQty   qty;
    BOM             qtyBom;
    Qty             totalQty;
    Qty             availQty;
    InventOnHandQty inventOnHandQty;
    InventDim       inventDimQty;
    InventDimParm   inventDimParmQty;

    if (_bom.PmfPlanGroupId)
    {
        while select bomQty, bomQtySerie
            from qtyBom
            where qtyBom.bomId == _bom.bomId
                && qtyBom.PmfPlanGroupId == _bom.PmfPlanGroupId
        {
            totalQty += qtyBom.bomQty() * planItemQty;
        }

        while select ItemId, UnitId, InventDimId
            from qtyBom
            where qtyBom.bomId == _bom.bomId
                && qtyBom.PmfPlanGroupId == _bom.PmfPlanGroupId
                && qtyBom.PmfPlanGroupPriority <= _bom.PmfPlanGroupPriority
        {
            inventDimQty = qtyBom.inventDim();
            inventDimParmQty.initFromInventDim(inventDimQty);
            inventOnHandQty =  InventOnHandQty::newItemDim(qtyBom.ItemId, inventDimQty, inventDimParmQty);

            availQty = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(_bom.ItemId,
                                                                                             inventDimQty.inventDimId,
                                                                                             inventOnHandQty.availPhysical(),
                                                                                             _bom.inventTable().inventUnitId(),
                                                                                             _bom.UnitId,
                                                                                             NoYes::Yes);

            if (totalQty - availQty < 0)
            {
                availQty = totalQty;
                totalQty = 0;
            }
            else
            {
                totalQty -= availQty;
            }
        }

        qty = availQty;
    }
    else
    {
        qty = _bom.bomQty() * planItemQty;
    }

    return qty;
}

