public void executeQuery()
{
    MapIterator             mi;

    ReqPO                   reqPoTmp;

    PmfRecIdList            recIdList;

    QueryBuildDataSource    qbs;
    QueryBuildRange         qbsRange;
    QueryBuildRange         qbsItem;

    Integer                 rangeCount;

    #DEFINE.MaxInList(230)

    void addRange()
    {
        qbsRange = null;

        if (rangeCount)
        {
            qbsRange = qbs.findRange(fieldNum(ReqPO,RecId),rangeCount);

            rangeCount--;
        }

        if (! qbsRange)
        {
            qbsRange = qbs.addRange(fieldNum(ReqPO,RecId));
        }

        qbsRange.value(recIdList);

        if (!qbsItem)
        {
            qbsItem = qbs.addRange(fieldNum(ReqPO,PmfPlanningItemId));
            qbsItem.value(SysQuery::valueNotEmptyString());
        }
    }

    qbs = reqPo_ds.query().dataSourceTable(tableNum(ReqPO));

    while (true)
    {
        rangeCount++;

        qbsRange = qbs.findRange(fieldNum(ReqPO,RecId),rangeCount);
        if (qbsRange)
        {
            qbsRange.value(SysQuery::valueUnlimited());
        }
        else
        {
            break;
        }
    }
    rangeCount--;

    if (mapReqPO)
    {
        mi = new MapIterator(mapReqPO);
        mi.begin();
        while (mi.more())
        {
            reqPoTmp = mapReqPO.lookup(mi.key());

            if (reqPoTmp.RecId && reqPoTmp.PmfPlanningItemId != '')
            {
                if (!recIdList)
                {
                    recIdList += queryValue(reqPoTmp.RecId);
                }
                else
                {
                    recIdList += ','+queryValue(reqPoTmp.RecId);
                }

                if (strLen(recIdList) > #MaxInList)
                {
                    addRange();
                    recIdList = '';
                }
            }
            mi.next();
        }

        if (!recIdList)
        {
            recIdList = queryValue(0);
        }

        addRange();
    }

    super();
}

