public display Qty qty(ReqTrans _reqTrans)
{
    select firstonly tmpReqTransSettled
        where tmpReqTransSettled.ReqTransRefRecId == _reqTrans.RecId;

    return tmpReqTransSettled.Qty;
}

public void clicked()
{
    element.createMasterPlan();
    element.close();
}

public void lookup()
{
    InventTable::lookupFormulaItemId(this, false, reqPO.ItemId);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.planningItemModified();

    return ret;
}

public boolean validate()
{
    ReqPO   reqPOTmp;
    boolean ret;

    ret = super();

    // Use the existing validation logic in the ReqPO table
    reqPOTmp.data(reqPO);
    reqPOTmp.PmfPlanningItemId = planningItemId.text();
    ret = ret && reqPOTmp.validateFieldValue(fieldStr(ReqPO, PmfPlanningItemId));

    return ret;
}

/// <summary>
/// Lookup formula versions for the selected planning item
/// </summary>
public void lookup()
{
    BOMVersion::lookupBOMFormula(this, PlanningItemId.text());
}

/// <summary>
/// Updates related co-product and formula lines when the BOM version selection is changed
/// </summary>
/// <returns>true if modification was successful; false otherwise</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    element.bomModified();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    formulaExplode_ds.executeQuery();
    pmfFormulaCoByProduct_ds.executeQuery();
    element.calcPegging();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.planItemQty(false, planItemQty);
    formulaExplode_ds.executeQuery();
    pmfFormulaCoByProduct_ds.executeQuery();
    element.calcPegging();

    return ret;
}

public void clicked()
{
    super();

    element.addOrder();
}

