[Form]
    /// <summary>
    /// This form shows the Conslidated orders
    /// </summary>
    public class PmfConsOrder extends FormRun
    {
        NumberSeqFormHandler        numberSeqFormHandler;
        InventDimCtrl_Frm           bulkItemInventDimFormSetup;
        InventDimCtrl_Frm           packItemInventDimFormSetup;

