public void init()
{
    super();

    dimensionDisplayBulkOrder = false;
    element.updateDesign(InventDimFormDesignUpdate::Init, false, false);

    if (ProdApplyUserTimezoneOffsetFlight::instance().isEnabled())
    {
        this.showTimeWithUserTimezone(ProdParameters::find().ShowSchedDateTimeWithUserTimeZone);
    }
}

private void showTimeWithUserTimezone(boolean showWithTimezone)
{
    DateScheduled.visible(!showWithTimezone);
    DateScheduledTimezone.visible(showWithTimezone);
}

/// <summary>
///     Retrieves the dimension setup object used in the form for bulk and pack orders.
/// </summary>
/// <returns>
///     The object of the <c>InventDimCtrl_Frm</c> class initialized for either the bulk or pack orders.
/// </returns>
public InventDimCtrl_Frm inventDimSetupObject()
{
    if (dimensionDisplayBulkOrder)
    {
        return bulkItemInventDimFormSetup;
    }
    else
    {
        return packItemInventDimFormSetup;
    }
}

public NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(
            ProdParameters::pmfNumRefConsOrdId().NumberSequenceId,
            element,
            pmfConsOrd_DS, fieldnum(PmfConsOrd, ConsOrdId));
    }

    return numberSeqFormHandler;
}

/// <summary>
///     Updates the dimension display on the form based on the selected records.
/// </summary>
/// <param name="_mode">
///     The update mode of the dimension controller.
/// </param>
/// <param name="_bulkChange">
///     A boolean value indicating whether to update the bulk items dimension display.
/// </param>
/// <param name="_packChange">
///     A boolean value indicating whether to update the packed items dimension display.
/// </param>
/// <exception cref="Exception::Error">
///     Method is called with the invalid update mode.
/// </exception>
public void updateDesign(InventDimFormDesignUpdate _mode, boolean _bulkChange, boolean _packChange)
{
    // bulk
    InventDimGroupSetup bulkInventDimGroupSetup;
    InventDimParm       bulkInventDimParmShow;
    InventDimParm       bulkInventDimParmEnable;
    Set                 bulkDimSet;
    // pack
    InventDimGroupSetup packInventDimGroupSetup;
    InventDimParm       packInventDimParmShow;
    InventDimParm       packInventDimParmEnable;
    Set                 packDimSet;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :
            // bulk
            if (!bulkItemInventDimFormSetup)
            {
                bulkInventDimParmShow.initProductDimensionsAllGroups();
                bulkInventDimParmEnable.data(bulkInventDimParmShow);

                bulkDimSet = new Set(Types::String);
                bulkDimSet.add(bulkOrders_DS.name());
                bulkDimSet.add(bulkDim_DS.name());
                bulkItemInventDimFormSetup  = InventDimCtrl::newFromFormValidDataSources(this, bulkDimSet);
            }

            bulkItemInventDimFormSetup.parmDimParmEnabled(bulkInventDimParmEnable);
            bulkItemInventDimFormSetup.parmDimParmVisibleGrid(bulkInventDimParmShow);
            bulkItemInventDimFormSetup.formSetControls(true);

            // pack
            if (!packItemInventDimFormSetup)
            {
                packInventDimParmShow.initProductDimensionsAllGroups();
                packInventDimParmEnable.data(packInventDimParmShow);

                packDimSet = new Set(Types::String);
                packDimSet.add(packOrders_DS.name());
                packDimSet.add(packDim_DS.name());
                packItemInventDimFormSetup  = InventDimCtrl::newFromFormValidDataSources(this, packDimSet);
            }
            packItemInventDimFormSetup.parmDimParmEnabled(packInventDimParmEnable);
            packItemInventDimFormSetup.parmDimParmVisibleGrid(packInventDimParmShow);
            packItemInventDimFormSetup.formSetControls(true);

        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::LinkActive,
             InventDimFormDesignUpdate::FieldChange :

            // bulk
            if (_bulkChange)
            {
                bulkInventDimGroupSetup = InventDimGroupSetup::newInventTable(bulkOrders.inventTable());
                bulkInventDimParmEnable = bulkInventDimGroupSetup.ecoResProductDimGroupSetup().inventDimParmActiveDimensions();
                bulkItemInventDimFormSetup.parmDimParmEnabled(bulkInventDimParmEnable);
                bulkItemInventDimFormSetup.formSetControls(true);
            }

            // pack
            if (_packChange)
            {
                packInventDimGroupSetup = InventDimGroupSetup::newInventTable(packOrders.inventTable());
                packInventDimParmEnable = packInventDimGroupSetup.ecoResProductDimGroupSetup().inventDimParmActiveDimensions();
                packItemInventDimFormSetup.parmDimParmEnabled(packInventDimParmEnable);
                packItemInventDimFormSetup.formSetControls(true);
            }
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

/// <summary>
/// Updates the <c>PmfConsOrd</c> with info from bulk/pack orders.
/// </summary>
public void updateHeader()
{
    InventTable             inventTable;
    ProdTable               prodTable;
    ReqDateOrder            reqDateOrder  = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    ReqTimeOrder            reqTimeOrder;
    ReqDateDlv              reqDateDlv    = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    ReqTime                 reqTime;
    Qty                     packQty       = 0;
    BOM                     bomBulkItem;
    BOMVersion              bomVersion;
    BOMCalcConsumption      bomCalcConsumption;

    if (bulkOrders.RecId)
    {
        inventTable             = InventTable::find(bulkOrders.ItemId);
        pmfConsOrd.ItemName     = inventTable.itemName();

        select sum(QtySched)
            from prodTable
            where  prodTable.PmfConsOrdId == pmfConsOrd.ConsOrdId
                && prodTable.PmfBulkOrd;

        pmfConsOrd.ConsQty      = prodTable.QtySched;
        pmfConsOrd.bomUnitId    = inventTable.bomUnitId;
    }

    while select prodTable
        where  prodTable.PmfConsOrdId == pmfConsOrd.ConsOrdId
            && !prodTable.PmfBulkOrd
    {
        if (prodTable.SchedStart < reqDateOrder)
        {
            reqDateOrder = prodTable.SchedStart;
            reqTimeOrder = prodTable.SchedFromTime;
        }

        if (reqDateOrder == prodTable.SchedStart
            && prodTable.SchedFromTime < reqTimeOrder)
        {
            reqTimeOrder = prodTable.SchedFromTime;
        }

        if (prodTable.SchedEnd > reqDateDlv)
        {
            reqDateDlv = prodTable.SchedEnd;
            reqTime    = prodTable.SchedToTime;
        }

        if (prodTable.SchedEnd  == reqDateDlv
            && prodTable.SchedToTime > reqTime)
        {
            reqTime = prodTable.SchedToTime;
        }
        if (!bulkOrders.RecId)
        {
            bomVersion = BOMVersion::findApproved(
                prodTable.ItemId,
                prodTable.bomId,
                true,
                prodTable.bomDate,
                prodTable.bomDate,
                prodTable.QtySched);

            select firstonly bomBulkItem
                where  bomBulkItem.bomId    == bomVersion.bomId
                    && bomBulkItem.ItemId   == bomVersion.PmfBulkParent;

            inventTable = InventTable::find(prodTable.ItemId);

            bomCalcConsumption  = BOMCalcConsumption::construct(
                BOMFormula::Formula0,
                BOMCalcData::newInventTable(
                    prodTable.QtySched,
                    inventTable),
                bomVersion.PmfYieldPct,
                true);
            packQty += bomCalcConsumption.calcConsumption(bomBulkItem);
        }
    }

    if (packQty)
    {
        pmfConsOrd.ConsQty = packQty;
    }
    pmfConsOrd.ReqDateOrder = reqDateOrder;
    pmfConsOrd.ReqTimeOrder = reqTimeOrder;
    pmfConsOrd.ReqDateDlv   = reqDateDlv;
    pmfConsOrd.ReqTime      = reqTime;
    pmfConsOrd_ds.write();
    pmfConsOrd_ds.refresh();
}

