/// <summary>
/// This method is called when the datasource will run the query to get the data
/// </summary>
public void executeQuery()
{
    QueryBuildRange qbr;

    qbr = this.query()
            .dataSourceNo(1)
            .findRange(fieldnum(PmfConsOrd, ProdStatus));
    if (!qbr)
    {
        qbr = this.query()
                .dataSourceNo(1)
                .addRange(fieldnum(PmfConsOrd, ProdStatus));
    }

    if (cbShow.checked())
    {
        qbr.value(queryRange(ProdStatus::Created,ProdStatus::ReportedFinished));
    }
    else
    {
        qbr.value(queryRange(ProdStatus::Created,ProdStatus::StartedUp));
    }

    super();

    if (element.args().dataset() == tablenum(PmfConsOrd))
    {
        PmfConsOrd_ds.positionToRecord(element.args().record());
    }
}

/// <summary>
/// This method returns the minimum back order status for the consolidated order
/// </summary>
/// <param name="_ord">Consolidated order</param>
/// <returns>returns back order status</returns>
public display ProdBackStatus backStatus(PmfConsOrd _ord)
{
    ProdTable prodTable;

    select minof(BackorderStatus)
        from prodTable
        where prodTable.PmfConsOrdId == _ord.ConsOrdId;

    return prodTable.BackorderStatus;
}

/// <summary>
/// This method returns the minimum status for the consolidated order
/// </summary>
/// <param name="_ord">Consolidated order</param>
/// <returns>returns order status</returns>
public display ProdStatus status(PmfConsOrd _ord)
{
    ProdTable prodTable;

    select minof(ProdStatus)
        from prodTable
        where prodTable.PmfConsOrdId == _ord.ConsOrdId;

    return prodTable.ProdStatus;
}

