/// <summary>
/// Called when the datasource is running the query to get the data
/// </summary>
public void executeQuery()
{
    QueryBuildRange qbrBulkOrd;
    QueryBuildRange qbrConsOrdId;

    qbrBulkOrd = this.query().dataSourceNo(1).findRange(fieldnum(ProdTable, PmfBulkOrd));

    if (!qbrBulkOrd)
    {
        qbrBulkOrd = this.query().dataSourceNo(1).addRange(fieldnum(ProdTable, PmfBulkOrd));
    }
    qbrBulkOrd.value(queryValue(NoYes::Yes));

    qbrConsOrdId = this.query()
            .dataSourceNo(1)
            .findRange(fieldnum(ProdTable, PmfConsOrdId));

    if (!qbrConsOrdId)
    {
        qbrConsOrdId = this.query()
                .dataSourceNo(1)
                .addRange(fieldnum(ProdTable, PmfConsOrdId));
    }

    qbrConsOrdId.value(SysQuery::valueNotEmptyString());

    super();
}

