/// <summary>
/// This method is called when the datasource will run the query to get the data
/// </summary>
public void executeQuery()
{
    QueryBuildRange qbr;

    qbr = this.query()
            .dataSourceNo(1)
            .findRange(fieldnum(ProdTable, PmfBulkOrd));

    if (!qbr)
    {
        qbr= this.query()
                .dataSourceNo(1)
                .addRange(fieldnum(ProdTable, PmfBulkOrd));
    }

    qbr.value(queryValue(NoYes::No));

    qbr = this.query()
            .dataSourceNo(1)
            .findRange(fieldnum(ProdTable, PmfConsOrdId));
    if (!qbr)
    {
        qbr= this.query()
                .dataSourceNo(1)
                .addRange(fieldnum(ProdTable, PmfConsOrdId));
    }

    qbr.value(SysQuery::valueNot(""));

    super();
}

