/// <summary>
/// This method is called when the update button is clicked and updates the submenu items as appropriate
/// </summary>
void clicked()
{
    Common          anyBuffer;
    ProdStatusType  prodStatus;

    if (bulkOrders.RecId)
    {
        prodStatus = bulkOrders.statusClient();

        ProdStatusParameters::checkExistCreate();

        costEstimationButton.enabled(
            prodStatus.runCostEstimation(anyBuffer,true));
        masterSchedulingButton.enabled(
            prodStatus.runOperationScheduling(anyBuffer,true));
        detailSchedulingButton.enabled(
            prodStatus.runJobScheduling(anyBuffer,true));
        releaseButton.enabled(prodStatus.runRelease(anyBuffer,true));
        startUpButton.enabled(prodStatus.runStartUp(anyBuffer,true));
        reportFinishedButton.enabled(
            prodStatus.runReportFinished(anyBuffer,true));
        endButton.enabled(prodStatus.runHistoricalCost(anyBuffer,true));
        priceCalculationButton.enabled(prodStatus.runBOMCalc(anyBuffer,true));
        statusDecreaseButton.enabled(prodStatus.runStatusDecrease(null,true));
        splitButton.enabled(prodStatus.runSplit(null,true));

        bulkOrders_DS.reread();
        bulkOrders_DS.refresh();
    }
    else
    {
        costEstimationButton.enabled(false);
        masterSchedulingButton.enabled(false);
        detailSchedulingButton.enabled(false);
        releaseButton.enabled(false);
        startUpButton.enabled(false);
        reportFinishedButton.enabled(false);
        endButton.enabled(false);
        priceCalculationButton.enabled(false);
        statusDecreaseButton.enabled(false);
        splitButton.enabled(false);
    }

    super();
}

