/// <summary>
/// This method is called when the Add to consolidated order is clicked
/// </summary>
void clicked()
{
    FormRun formRun;
    Args    args;

    super();
    args = new Args(formstr(PmfAddToConsOrder));
    args.caller(element);
    args.record(pmfConsOrd);
    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();
    if (formRun.closedOk())
    {
        pmfConsOrd_ds.active();
    }
}

