/// <summary>
/// Add filter for BOMVersion
/// </summary>
/// <remarks>
/// Only add the filter if the BOMVersion is enabled to display the formula grid.
/// </remarks>
public void addBOMVersionFilter()
{
    if (displayHeader)
    {
        BOMVersion_ds.query().dataSourceTable(tablenum(BOMVersion)).rangeField(
            fieldnum(BOMVersion, RecId)).value(SysQuery::value(
                element.args().record().RecId));
    }
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    ret = ret && pmfFrmCtrl.canClosePost();

    return ret;
}

public void doRefresh()
{
    pmfFrmCtrl.doRefresh();
}

public void init()
{
    if (!element.args().caller())
    {
        throw error(strfmt("@SYS22678",formstr(PmfFormulaCoBy)));
    }

    pmfFrmCtrl = PmfFormCtrl_CoByProducts::newFromForm(element);

    pmfFrmCtrl.initPre();

    super();

    pmfFrmCtrl.setDatasources(pmfFormulaCoBy, inventDim);
    pmfFrmCtrl.initPost();

    switch (element.args().dataset())
    {
        case tablenum(BOMVersion) :
            displayHeader = true;
            break;
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

public void refreshCostAlloc()
{
    BOMVersion_ds.cacheCalculateMethod(tableMethodStr(BOMVersion, pmfRemainCostAlloc));
    remainCostAlloc.update();
    BOMVersion_ds.reread();
}

/// <summary>
/// Returns a collection of product types that are not allow in the <c>gridCoByProd</c> combo box.
/// </summary>
/// <returns>The collection of restricted product types</returns>
protected Set restrictedCoByProductTypes()
{
    Set set = new Set(Types::Enum);

    set.add(PmfProductType::None);
    set.add(PmfProductType::MainItem);
    set.add(PmfProductType::BOM);
    set.add(PmfProductType::Formula);

    return set;
}

public void run()
{
    super();

    Enumerator enum = this.restrictedCoByProductTypes().getEnumerator();
    while (enum.moveNext())
    {
        gridCoByProd_ProductType.delete(SysQuery::value(enum.current()));
    }
}

public void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParmShow;
    InventDimParm   inventDimParmEnabled;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :

            if (! inventDimFormSetup)
            {
                inventDimFormSetup
                    = InventDimCtrl::newFromForm(element);
            }

            inventDimFormSetup.parmSkipOnHandLookUp(true);
            inventDimParmShow.initDimBOMAll();
            inventDimParmShow.InventSiteIdFlag = false;
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmShow);
            //fallthrough
        case InventDimFormDesignUpdate::Active,
            InventDimFormDesignUpdate::FieldChange :
            InventDimGroupSetup::newInventTable(pmfFormulaCoBy.inventTable()).inventDimParmActive(inventDimParmEnabled);
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.formSetControls(true);
            break;

        default :
            throw error(strfmt("@SYS54195",funcname()));
    }

    FormulaTab.visible(displayHeader);
}

