[Form]
public class PmfMultiDimProcessInventory extends FormRun
{
    InventDimCtrl_Frm_ConfigUnitOnhand  inventDimFormSetup;
    [FormObservable, SysObsolete("converterInventToPmfUnit is deprecated, please use productConverterInventToPmfUnit instead.", false, 30\06\2020)]
    UnitOfMeasureConverter              converterInventToPmfUnit;
    [FormObservable]
    EcoResProductUnitConverter          productConverterInventToPmfUnit;
    InventTable                         selectedInventTable;
    QueryBuildRange                     qbrItemId;

    InventAvailabilityFormCache         inventAvailabilityFormCache;

    #define.CurrentVersion(1)
    #localmacro.CurrentList
            selectedInventTable
        #endmacro
