protected void calculateTotals()
{
    QueryRun                    qr;
    InventBatch                 inventBatchLocal;
    InventSum                   inventSumLocal;
    InventDim                   inventDimLocal;
    InventQty                   availPhysicalLocal;

    totalOnhand.realValue(0);
    unAvailable.realValue(0);
    available.realValue(0);

    EcoResProductUnitConverter converterInventToSelectedUnit = element.productConverterInventToSelectedUnit();

    if (converterInventToSelectedUnit)
    {
        qr = new QueryRun(inventSum_ds.query());

        // calculate total in selected unit of records in grid
        while (qr.next())
        {
            inventBatchLocal   = qr.get(tableNum(InventBatch));
            inventSumLocal     = qr.get(tableNum(InventSum));
            inventDimLocal     = qr.get(tableNum(InventDim));

            availPhysicalLocal = converterInventToSelectedUnit.convertValue(
                                        inventAvailabilityFormCache.availability(inventSumLocal,inventDimLocal).availPhysical());

            if (   inventBatchLocal.PdsDispositionCode
                && PdsDispositionMaster::find(inventBatchLocal.PdsDispositionCode).Status == PdsStatus::Unavailable)
            {
                unAvailable.realValue(unAvailable.realValue() + availPhysicalLocal);
            }

            totalOnhand.realValue(totalOnhand.realValue() + availPhysicalLocal);
        }

        available.realValue(totalOnhand.realValue() - unAvailable.realValue());
    }
}

/// <summary>
/// Creates an instance of <c>EcoResProductUnitConverter</c> from the selected invent table's unit of measure
/// to a selected unit of measure.
/// </summary>
/// <returns>
/// An instance of <c>EcoResProductUnitConverter</c>.
/// </returns>
protected EcoResProductUnitConverter productConverterInventToSelectedUnit()
{
    EcoResProductUnitConverter productConverterInventToSelectedUnit;
    UnitOfMeasureRecId         inventUnitOfMeasureRecId   = UnitOfMeasure::unitOfMeasureIdBySymbol(selectedInventTable.inventUnitId());
    UnitOfMeasureRecId         selectedUnitOfMeasureRecId = UnitOfMeasure::unitOfMeasureIdBySymbol(selectedUnit.text());

    if (inventUnitOfMeasureRecId
        && selectedUnitOfMeasureRecId)
    {
        productConverterInventToSelectedUnit = EcoResProductUnitConverter::newForReleasedProduct(selectedInventTable.ItemId,
                                                                                                 inventSum.InventDimId,
                                                                                                 inventUnitOfMeasureRecId,
                                                                                                 selectedUnitOfMeasureRecId,
                                                                                                 NoYes::Yes,
                                                                                                 NoYes::No);

        if (!productConverterInventToSelectedUnit.canValueBeConverted())
        {
            productConverterInventToSelectedUnit = null;
        }
    }

    return productConverterInventToSelectedUnit;
}

void doRefresh()
{
    InventBatch             pmfInventBatch;

    pmfInventBatch = inventBatch.data();
    inventBatch_ds.research();
    inventBatch_ds.positionToRecord(pmfInventBatch);
}

void init()
{
    super();

    inventAvailabilityFormCache = InventAvailabilityFormCache::construct();

    if (! inventSum_DS.query().dataSourceTable(tableNum(InventSum)).dynalinkCount())
    {
        qbrItemId = inventSum_DS.query().dataSourceTable(tableNum(InventSum)).addRange(fieldNum(InventSum,ItemId));
        xSysLastValue::getLast(this);
        if (selectedInventTable)
        {
            productNumber.text(selectedInventTable.ItemId);
        }

        productNumber.modified();
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);
    element.updateDesignBatchFields();
}

void initParmDefault()
{
    EcoResProductMasterConfiguration    ecoResProductMasterConfiguration;

    // select first formula product with configuration definition
    select firstonly selectedInventTable
        order by ItemId
        where selectedInventTable.PmfProductType == PmfProductType::Formula
        exists join ecoResProductMasterConfiguration
            where  ecoResProductMasterConfiguration.ConfigProductMaster             == selectedInventTable.Product
                && ecoResProductMasterConfiguration.ConfigProductDimensionAttribute == EcoResProductDimensionAttribute::inventDimFieldId2DimensionAttributeRecId(fieldNum(InventDim, ConfigId));
}

InventDimCtrl_Frm_ConfigUnitOnhand inventDimSetupObject()
{
    if (!inventDimFormSetup)
    {
        inventDimFormSetup  = InventDimCtrl_Frm_ConfigUnitOnhand::newFromForm(element);
        inventDimFormSetup.parmLargeRightClickForm(NoYes::Yes);
    }
    return inventDimFormSetup;
}

public boolean setConvertInventToPmfUnit()
{
    UnitOfMeasureRecId      inventUnitOfMeasureRecId = UnitOfMeasure::unitOfMeasureIdBySymbol(selectedInventTable.inventUnitId());
    UnitOfMeasureRecId      pmfUnitOfMeasureRecId = UnitOfMeasure::unitOfMeasureIdBySymbol(pmfConfigUnitId.text());

    if (productConverterInventToPmfUnit
        && productConverterInventToPmfUnit.parmFromUnitOfMeasureRecId() == inventUnitOfMeasureRecId
        && productConverterInventToPmfUnit.parmToUnitOfMeasureRecId()   == pmfUnitOfMeasureRecId)
    {
        return true;
    }

    if (   inventUnitOfMeasureRecId
        && pmfUnitOfMeasureRecId)
    {
        productConverterInventToPmfUnit = EcoResProductUnitConverter::newForReleasedProduct(selectedInventTable.ItemId,
                                                                                            inventSum.InventDimId,
                                                                                            inventUnitOfMeasureRecId,
                                                                                            pmfUnitOfMeasureRecId,
                                                                                            NoYes::Yes);

        if (!productConverterInventToPmfUnit.canValueBeConverted())
        {
            productConverterInventToPmfUnit = null;
        }
    }
    else
    {
        productConverterInventToPmfUnit = null;
    }

    return productConverterInventToPmfUnit != null;
}

public boolean unpack(container packedClass)
{
    Integer     version = conPeek(packedClass,1);

    switch (version)
    {
        case #CurrentVersion    :
            [version,#CurrentList] = packedClass;
            break;
        default :
            return false;
    }

    return true;
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm       inventDimParmLocked;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:

            inventDimParmLocked.ClosedQtyFlag = NoYes::Yes;
            inventDimParmLocked.ConfigIdFlag = NoYes::Yes;
            element.inventDimSetupObject().parmDimParmLockedRightClick(inventDimParmLocked);
            element.inventDimSetupObject().parmDimParmVisibleGrid(inventDimParmLocked.data());
        // no break fall through

        case InventDimFormDesignUpdate::Active:
            element.inventDimSetupObject().formSetControls(true,true);
            break;
    }
}

public void linkActive()
{
    #define.firstLink(1)
    QueryBuildDataSource    qbds;
    QueryBuildDynalink      dynaLink;
    Common                  common;

    super();

    qbds = inventSum_ds.query().dataSourceTable(tableNum(InventSum));

    if (   qbds
        && qbds.dynalinkCount())
    {
        dynaLink = inventSum_q.dataSourceTable(tableNum(InventSum)).dynalink(#firstLink);
        common = dynaLink.cursor();

        selectedInventTable = InventTable::find(common.(dynaLink.dynamicField()));

        productNumber.text(selectedInventTable.ItemId);
        productNumber.allowEdit(false);

        element.updateSelectedItem();
    }
}

display PmfInventQtyAvailPhysical pmfUnitAvailPhysical(InventSum    _inventSum)
{
    if (element.setConvertInventToPmfUnit())
    {
        return productConverterInventToPmfUnit.convertValue(inventAvailabilityFormCache.availability(_inventSum, this.getRelatedInventDim(_inventSum)).availPhysical());
    }
    return 0;
}

display PmfConfigUnitId pmfConfigUnitId(InventDim   _inventDim)
{
    EcoResConfigurationRecId    ecoResConfigurationRecId;

    ecoResConfigurationRecId = EcoResConfiguration::findByName(_inventDim.ConfigId).RecId;

    if (ecoResConfigurationRecId)
    {
        return EcoResProductMasterConfiguration::find(selectedInventTable.Product, ecoResConfigurationRecId).PmfConfigUnitId;
    }

    return '';
}

public void init()
{
    QueryBuildDataSource    qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(InventBatch));

    if (qbds)
    {
        qbds.addLink(fieldNum(InventSum, ItemId), fieldNum(InventBatch, ItemId), inventSum_ds.name());
        qbds.addSortField(fieldNum(InventBatch, PdsDispositionCode));
        qbds.orderMode(OrderMode::GroupBy);
    }
}

public void lookup()
{
    SysTableLookup          sysTableLookup                          = SysTableLookup::newParameters(tableNum(InventTable),this);
    Query                   query                                   = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    RefRecId                ecoResProductDimensionAttributeRecId    = EcoResProductDimensionAttribute::inventDimFieldId2DimensionAttributeRecId(fieldNum(InventDim, ConfigId));

    sysTableLookup.addLookupfield(fieldNum(InventTable, ItemId));
    sysTableLookup.addLookupMethod(tableMethodStr(InventTable, itemGroupId));
    sysTableLookup.addLookupMethod(tableMethodStr(InventTable, defaultProductName));
    sysTableLookup.addLookupfield(fieldNum(InventTable, ItemType));
    sysTableLookup.addLookupfield(fieldNum(InventTable, PmfProductType));

    queryBuildDataSource = query.addDataSource(tableNum(InventTable));
    queryBuildDataSource.addRange(fieldNum(InventTable, PmfProductType)).value(queryValue(PmfProductType::Formula));
    queryBuildDataSource.addSortField(fieldNum(InventTable, ItemId));

    queryBuildDataSource = queryBuildDataSource.addDataSource(tableNum(EcoResProductMasterConfiguration));
    queryBuildDataSource.addLink(fieldNum(InventTable, Product),fieldNum(EcoResProductMasterConfiguration,ConfigProductMaster));
    queryBuildDataSource.addRange(fieldNum(EcoResProductMasterConfiguration, ConfigProductDimensionAttribute)).value(queryValue(ecoResProductDimensionAttributeRecId));
    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    InventTable newSelectedInventTable;
    boolean     ret;

    ret = super();

    if (ret && this.text())
    {
        newSelectedInventTable = InventTable::find(this.text());

        if (!newSelectedInventTable.RecId)
        {
            ret = checkFailed("@PRO260");
        }

        if (   ret
            && !newSelectedInventTable.configActive())
        {
            ret = checkFailed(strFmt("@SYS25621",this.text()));
        }

        if (   ret
            && newSelectedInventTable.PmfProductType != PmfProductType::Formula)
        {
            ret = checkFailed(strFmt("@PRO154",this.text()));
        }
    }

    if (ret)
    {
        selectedInventTable = newSelectedInventTable;
        qbrItemId.value(queryValue(selectedInventTable.ItemId));
        inventSum_ds.executeQuery();

        element.updateSelectedItem();
    }
    else
    {
        this.text(selectedInventTable.ItemId);
    }

    return ret;
}

public boolean modified()
{
    boolean     ret;
    str         textOrig = this.text();

    ret = super();

    if (ret
        && !EcoResProductUnitConverter::canBeConvertedGivenUnitSymbolsForReleasedProduct(inventSum.ItemId,
                                                                                         inventSum.InventDimId,
                                                                                         selectedInventTable.inventUnitId(),
                                                                                         this.text()))
    {
        ret = checkFailed(strFmt("@SYS28690",selectedInventTable.inventUnitId(),this.text()));
        this.text(textOrig);
    }
    else
    {
        element.applySelectedUnit();
    }

    return ret;
}
