[Form]
public class PmfProdCoBy extends FormRun
{
    #resAppl

    InventMovement                  inventMovement;
    InventDimCtrl_Frm_Mov           inventDimFormSetup;   
    PmfFormCtrl_ProdCoByProducts    formCtrl;
    ProdTable                       prodTable;

}

public boolean canClose()
{
    boolean ret;

    ret = super();

    ret = ret && formCtrl.canClosePost();

    return ret;
}

void doReread()
{
    formCtrl.doReRead();
}

void doResearch()
{
    formCtrl.doResearch();
}

public void hideQualityManagement()
{
    qualityOrderStatusDisplay.visible(false);
    inventNonConformanceTable.visible(false);
    inventQualityOrderTable.visible(false);
}

void init()
{
    formCtrl = PmfFormCtrl_ProdCoByProducts::newFromForm(element);
    formCtrl.initPre();

    super();

    formCtrl.setDatasources(pmfProdCoBy, inventDim);
    formCtrl.initPost();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (!InventParameters::find().UseQualityManagement)
    {
        element.hideQualityManagement();
    }

    if (element.args().record().TableId == tablenum(ProdTable))
    {
        prodTable = element.args().record();
    }
    this.setFormFilters();
}

private void setFormFilters()
{
    var qbd = PmfProdCoBy_DS.queryBuildDataSource();
    var qFilter = PmfProdCoBy_DS.query().addQueryFilter(qbd, fieldStr(PmfProdCoBy, ItemId));
    var qFilter2 = PmfProdCoBy_DS.query().addQueryFilter(qbd, fieldStr(PmfProdCoBy, ProdId));
    qFilter2.status(RangeStatus::Hidden);
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

ProdId prodId()
{
    return formCtrl.prodId();
}

public void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm dimParmEnabled;
    inventMovement = InventMovement::construct(pmfProdCoBy);

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init          :

            if (!inventDimFormSetup)
            {
                inventDimFormSetup
                    = InventDimCtrl_Frm_Mov::newFromFormMovement(
                        element,
                        inventMovement);
            }

        case InventDimFormDesignUpdate::Active        :
            inventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(), inventMovement);
            dimParmEnabled = inventDimFormSetup.parmDimParmEnabled();
            dimParmEnabled.InventSiteIdFlag = false;
            inventDimFormSetup.parmDimParmEnabled(dimParmEnabled);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange   :
            inventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(), inventMovement);
            dimParmEnabled = inventDimFormSetup.parmDimParmEnabled();
            dimParmEnabled.InventSiteIdFlag = false;
            inventDimFormSetup.parmDimParmEnabled(dimParmEnabled);
            inventDimFormSetup.formSetControls(false);
            break;

        default :
            throw error(strfmt("@SYS54195",funcname()));
    }
}

public boolean validateWrite()
{
    boolean ret;

    ret = formCtrl.dataSourceValidateWrite();

    if (ret)
    {
        ret = super();
    }

    return ret;
}

public void initValue()
{
    super();

    formCtrl.dataSourceInitValuePost();
}

public void init()
{
    super();

    formCtrl.dataSourceInitPost(pmfProdCoBy);

    pmfProdCoBy_ds.cacheAddMethod(tablemethodstr(PmfProdCoBy, qualityOrderStatusDisplay));
    pmfProdCoBy_ds.cacheAddMethod(tablemethodstr(PmfProdCoBy, pdsCWUnitId));
}

void write()
{
    ttsbegin;

    formCtrl.dataSourceWritePre();

    super();

    formCtrl.dataSourceWritePost();

    inventDim_ds.setCurrent();

    ttscommit;
}

void linkActive()
{
    formCtrl.dataSourceLinkActivePre();

    super();
}

void create(boolean _append = false)
{
    if (prodTable && prodTable.status().isAfter(ProdStatus::Created))
    {
        throw error ("@PRO2321");
    }

    formCtrl.dataSourceCreatePre();

    super(_append);

    inventDim_ds.create();

    formCtrl.dataSourceCreatePost();
}

int active()
{
    int ret;
    boolean regulatedItem;

    ret =  super();
    regulatedItem = PdsMRCRegulatedCountry::isRegulated(pmfProdCoBy.ItemId);
    pdsMRCOpenMSDSDocument.enabled(regulatedItem);
    element.updateDesign(InventDimFormDesignUpdate::Active);
    formCtrl.dataSourceActivePost();

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    formCtrl.fieldLookup(fieldnum(PmfProdCoBy, ItemId), _formControl, _filterStr);
}

void modified()
{
    super();

    pmfProdCoBy_ds.cacheCalculateMethod(tablemethodstr(PmfProdCoBy, pdsCWUnitId));

    inventDim.data(InventDim::find(pmfProdCoBy.InventDimId));
    element.updateDesign(InventDimFormDesignUpdate::FieldChange);
    formCtrl.fieldModified(fieldnum(PmfProdCoBy,ItemId));
}

public void modified()
{
    super();

    formCtrl.fieldModified(fieldnum(PmfProdCoBy, PdsCWQty));
}

void modified()
{
    InventMovement::setMinMaxQty(pmfProdCoBy);
    InventMovement::bufferSetTransQtyUnit(pmfProdCoBy);
    super();
    formCtrl.fieldModified(fieldnum(PmfProdCoBy, QtyCoByCalc));
}

void clicked()
{
    super();
    Args args = new Args();
    args.caller(element);

    FormRun formRun = new MenuFunction(menuitemDisplayStr(PdsMRCDocument), MenuItemType::Display).create(args);
    formRun.run();
    formRun.wait();
}
