public void closeOk()
{
    if (priceDiscAdmSearch.checkCloseDialog())
    {
        if (!element.enableGenericCurrency())
        {
            genericCurrency = NoYes::No;
        }

        priceDiscAdmSearch.parmPriceTypeCriteria(this.priceTypeCriteria());
        priceDiscAdmSearch.parmCurrencyCode(currencyCode);
        priceDiscAdmSearch.parmGenericCurrency(genericCurrency);
        priceDiscAdmSearch.parmFromDate(fromDate);
        priceDiscAdmSearch.parmToDate(toDate);
        priceDiscAdmSearch.parmTmpPriceDiscAdmSearchCust(tmpPriceDiscAdmSearchCust.data());
        priceDiscAdmSearch.parmTmpPriceDiscAdmSearchVend(tmpPriceDiscAdmSearchVend.data());
        priceDiscAdmSearch.parmTmpPriceDiscAdmSearchInvent(tmpPriceDiscAdmSearchInvent.data());

        super();
    }
}

public Container priceTypeCriteria()
{
    Container priceTypeCriteria;

    if (prices)
    {
        priceTypeCriteria += [PriceType::PriceSales];
        priceTypeCriteria += [PriceType::PricePurch];
    }

    if (lineDiscounts)
    {
        priceTypeCriteria += [PriceType::LineDiscPurch];
        priceTypeCriteria += [PriceType::LineDiscSales];
    }

    if (multiLineDiscounts)
    {
        priceTypeCriteria += [PriceType::MultiLineDiscPurch];
        priceTypeCriteria += [PriceType::MultiLineDiscSales];
    }

    if (endDiscounts)
    {
        priceTypeCriteria += [PriceType::EndDiscSales];
        priceTypeCriteria += [PriceType::EndDiscPurch];
    }

    return priceTypeCriteria;
}

