public void enableAgreementFields_RU()
{
    TmpPriceDiscAdmSearchCust_ds.object(fieldnum(TmpPriceDiscAdmSearch, AgreementHeaderExt_RU)).allowEdit(
        TmpPriceDiscAdmSearchCust.AccountCode == PriceDiscPartyCodeType::Table &&
        TmpPriceDiscAdmSearchCust.AccountRelation);

    TmpPriceDiscAdmSearchVend_ds.object(fieldnum(TmpPriceDiscAdmSearch, AgreementHeaderExt_RU)).allowEdit(
        TmpPriceDiscAdmSearchVend.AccountCode == PriceDiscPartyCodeType::Table &&
        TmpPriceDiscAdmSearchVend.AccountRelation);
}

/// <summary>
/// Evaluates whether <c>GenericCurrency</c> should be enabled or disabled.
/// </summary>
/// <returns>
/// True if the <c>GenericCurrency</c> can be enabled; otherwise, false.
/// </returns>
public boolean enableGenericCurrency()
{
    SalesParameters salesParameters = SalesParameters::find();

    return salesParameters.GenericCurrency && currencyCode && currencyCode != salesParameters.GenericCurrency;
}

