public void init()
{
    priceDiscAdmSearch = element.args().caller().runbase();

    super();
    // <GEERU>
    countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    // </GEERU>

    dictEnum = new SysDictEnum(enumnum(PriceGroupType));

    fromDate                    = priceDiscAdmSearch.parmFromDate();
    toDate                      = priceDiscAdmSearch.parmToDate();
    currencyCode                = priceDiscAdmSearch.parmCurrencyCode();

    Container priceTypeCriteria = priceDiscAdmSearch.parmPriceTypeCriteria();

    prices = conFind(priceTypeCriteria, enum2int(PriceType::PricePurch)) > 0
        || conFind(priceTypeCriteria, enum2int(PriceType::PriceSales)) > 0;
    lineDiscounts = conFind(priceTypeCriteria, enum2int(PriceType::LineDiscPurch)) > 0
        || conFind(priceTypeCriteria, enum2int(PriceType::LineDiscSales)) > 0;
    multiLineDiscounts = conFind(priceTypeCriteria, enum2int(PriceType::MultiLineDiscPurch)) > 0
        || conFind(priceTypeCriteria, enum2int(PriceType::MultiLineDiscSales)) > 0;
    endDiscounts = conFind(priceTypeCriteria, enum2int(PriceType::EndDiscPurch)) > 0
        || conFind(priceTypeCriteria, enum2int(PriceType::EndDiscSales)) > 0;

    genericCurrency = priceDiscAdmSearch.parmGenericCurrency();

    if (prices)
    {
        this.updateDesignPriceGroupType(PriceGroupType::PriceGroup,true);
    }

    if (lineDiscounts)
    {
        this.updateDesignPriceGroupType(PriceGroupType::LineDiscGroup,true);
    }

    if (multiLineDiscounts)
    {
        this.updateDesignPriceGroupType(PriceGroupType::MultiLineDiscGroup,true);
    }

    if (endDiscounts)
    {
        this.updateDesignPriceGroupType(PriceGroupType::EndDiscGroup,true);
    }

    tmpPriceDiscAdmSearchCust_PriceGroupType.selectText(dictEnum.value2Label(enum2int(tmpPriceDiscAdmSearchCust.PriceDiscGroupType)));
    tmpPriceDiscAdmSearchVend_PriceGroupType.selectText(dictEnum.value2Label(enum2int(tmpPriceDiscAdmSearchVend.PriceDiscGroupType)));
    tmpPriceDiscAdmSearchInvent_PriceGroupType.selectText(dictEnum.value2Label(enum2int(tmpPriceDiscAdmSearchInvent.PriceDiscGroupType)));

    this.updateDesignOKButton();
    tmpPriceDiscAdmSelect_IncludePrices.setFocus();
    // <GEERU>
    if (countryRegion_RU)
    {
        element.enableAgreementFields_RU();
    }
    // </GEERU>
}

