public void updateDesignOKButton()
{
    // either a vendor or a customer relation must be specified, or All must be selected either
    // for customers or vendors
    if ( (!tmpPriceDiscAdmSearchCust.isForAll()
          && !tmpPriceDiscAdmSearchCust.AccountRelation
          && !tmpPriceDiscAdmSearchVend.isForAll()
          && !tmpPriceDiscAdmSearchVend.AccountRelation)

    // an item relation must be specified, or All must be selected
      || (!tmpPriceDiscAdmSearchInvent.isForAll()
          && !tmpPriceDiscAdmSearchInvent.ItemRelation)

    // at least one trade agreement type must be selected
      || (!prices && !lineDiscounts && !multiLineDiscounts && !endDiscounts))

    {
        commandButtonOK.enabled(false);
    }
    else
    {
        commandButtonOK.enabled(true);
    }
}

public void updateDesignPriceGroupType(PriceGroupType _priceGroupType, boolean _set = true)
{
    switch (_priceGroupType)
    {
        case PriceGroupType::EndDiscGroup :
            if (_set)
            {
                tmpPriceDiscAdmSearchCust_PriceGroupType.add(dictEnum.value2Label(enum2int(PriceGroupType::EndDiscGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.add(dictEnum.value2Label(enum2int(PriceGroupType::EndDiscGroup)));

                tmpPriceDiscAdmSearchCust_PriceGroupType.selectText(dictEnum.value2Label(enum2int(PriceGroupType::EndDiscGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.selectText(dictEnum.value2Label(enum2int(PriceGroupType::EndDiscGroup)));

                tmpPriceDiscAdmSearchCust_PriceGroupType.modified();
                tmpPriceDiscAdmSearchVend_PriceGroupType.modified();
            }
            else
            {
                tmpPriceDiscAdmSearchCust_PriceGroupType.delete(dictEnum.value2Label(enum2int(PriceGroupType::EndDiscGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.delete(dictEnum.value2Label(enum2int(PriceGroupType::EndDiscGroup)));
            }
        break;

        case PriceGroupType::LineDiscGroup :
            if (_set)
            {
                tmpPriceDiscAdmSearchCust_PriceGroupType.add(dictEnum.value2Label(enum2int(PriceGroupType::LineDiscGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.add(dictEnum.value2Label(enum2int(PriceGroupType::LineDiscGroup)));
                tmpPriceDiscAdmSearchInvent_PriceGroupType.add(dictEnum.value2Label(enum2int(PriceGroupType::LineDiscGroup)));

                tmpPriceDiscAdmSearchCust_PriceGroupType.selectText(dictEnum.value2Label(enum2int(PriceGroupType::LineDiscGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.selectText(dictEnum.value2Label(enum2int(PriceGroupType::LineDiscGroup)));
                tmpPriceDiscAdmSearchInvent_PriceGroupType.selectText(dictEnum.value2Label(enum2int(PriceGroupType::LineDiscGroup)));

                tmpPriceDiscAdmSearchCust_PriceGroupType.modified();
                tmpPriceDiscAdmSearchVend_PriceGroupType.modified();
                tmpPriceDiscAdmSearchInvent_PriceGroupType.modified();
            }
            else
            {
                tmpPriceDiscAdmSearchCust_PriceGroupType.delete(dictEnum.value2Label(enum2int(PriceGroupType::LineDiscGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.delete(dictEnum.value2Label(enum2int(PriceGroupType::LineDiscGroup)));
                tmpPriceDiscAdmSearchInvent_PriceGroupType.delete(dictEnum.value2Label(enum2int(PriceGroupType::LineDiscGroup)));
            }
        break;

        case PriceGroupType::MultiLineDiscGroup :
            if (_set)
            {
                tmpPriceDiscAdmSearchCust_PriceGroupType.add(dictEnum.value2Label(enum2int(PriceGroupType::MultiLineDiscGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.add(dictEnum.value2Label(enum2int(PriceGroupType::MultiLineDiscGroup)));
                tmpPriceDiscAdmSearchInvent_PriceGroupType.add(dictEnum.value2Label(enum2int(PriceGroupType::MultiLineDiscGroup)));

                tmpPriceDiscAdmSearchCust_PriceGroupType.selectText(dictEnum.value2Label(enum2int(PriceGroupType::MultiLineDiscGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.selectText(dictEnum.value2Label(enum2int(PriceGroupType::MultiLineDiscGroup)));
                tmpPriceDiscAdmSearchInvent_PriceGroupType.selectText(dictEnum.value2Label(enum2int(PriceGroupType::MultiLineDiscGroup)));

                tmpPriceDiscAdmSearchCust_PriceGroupType.modified();
                tmpPriceDiscAdmSearchVend_PriceGroupType.modified();
                tmpPriceDiscAdmSearchInvent_PriceGroupType.modified();
            }
            else
            {
                tmpPriceDiscAdmSearchCust_PriceGroupType.delete(dictEnum.value2Label(enum2int(PriceGroupType::MultiLineDiscGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.delete(dictEnum.value2Label(enum2int(PriceGroupType::MultiLineDiscGroup)));
                tmpPriceDiscAdmSearchInvent_PriceGroupType.delete(dictEnum.value2Label(enum2int(PriceGroupType::MultiLineDiscGroup)));
            }
        break;

        case PriceGroupType::PriceGroup :
            if (_set)
            {
                tmpPriceDiscAdmSearchCust_PriceGroupType.add(dictEnum.value2Label(enum2int(PriceGroupType::PriceGroup)));
                tmpPriceDiscAdmSearchCust_PriceGroupType.selectText(dictEnum.value2Label(enum2int(PriceGroupType::PriceGroup)));

                tmpPriceDiscAdmSearchVend_PriceGroupType.add(dictEnum.value2Label(enum2int(PriceGroupType::PriceGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.selectText(dictEnum.value2Label(enum2int(PriceGroupType::PriceGroup)));

                tmpPriceDiscAdmSearchCust_PriceGroupType.modified();
                tmpPriceDiscAdmSearchVend_PriceGroupType.modified();
            }
            else
            {
                tmpPriceDiscAdmSearchCust_PriceGroupType.delete(dictEnum.value2Label(enum2int(PriceGroupType::PriceGroup)));
                tmpPriceDiscAdmSearchVend_PriceGroupType.delete(dictEnum.value2Label(enum2int(PriceGroupType::PriceGroup)));
            }
        break;
    }

    if (!tmpPriceDiscAdmSearchCust_PriceGroupType.valueStr()
        &&  tmpPriceDiscAdmSearchCust_PriceGroupType.items())
    {
        tmpPriceDiscAdmSearchCust_PriceGroupType.selection(tmpPriceDiscAdmSearchCust_PriceGroupType.items()-1);
        tmpPriceDiscAdmSearchCust_PriceGroupType.modified();
    }
    if (!tmpPriceDiscAdmSearchVend_PriceGroupType.valueStr()
        &&  tmpPriceDiscAdmSearchVend_PriceGroupType.items())
    {
        tmpPriceDiscAdmSearchVend_PriceGroupType.selection(tmpPriceDiscAdmSearchVend_PriceGroupType.items()-1);
        tmpPriceDiscAdmSearchVend_PriceGroupType.modified();
    }
    if ((_priceGroupType == PriceGroupType::LineDiscGroup
        || _priceGroupType == PriceGroupType::MultiLineDiscGroup)
        &&  !tmpPriceDiscAdmSearchInvent_PriceGroupType.valueStr()
        &&  tmpPriceDiscAdmSearchInvent_PriceGroupType.items())
    {
        tmpPriceDiscAdmSearchInvent_PriceGroupType.selection(tmpPriceDiscAdmSearchInvent_PriceGroupType.items()-1);
        tmpPriceDiscAdmSearchInvent_PriceGroupType.modified();
    }
}

