/// <summary>
/// Enables the generic currency check box.
/// </summary>
/// <returns>
/// True if the modification was successful; otherwise, false.
/// </returns>
public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        genericCurrencyBox.skip(!genericCurrencyBox.allowEdit(element.enableGenericCurrency()));
    }

    return ret;
}

public boolean modified()
{
    boolean ret;
    PriceGroupType priceGroup;

    ret = super();

    switch (this.valueStr())
    {
        case enum2str(PriceGroupType::PriceGroup):
             tmpPriceDiscAdmSearchCust.Relation       = PriceType::PriceSales;
             break;

        case enum2str(PriceGroupType::LineDiscGroup):
             tmpPriceDiscAdmSearchCust.Relation       =  PriceType::LineDiscSales;
             break;

        case enum2str(PriceGroupType::MultiLineDiscGroup):
             tmpPriceDiscAdmSearchCust.Relation       =  PriceType::MultiLineDiscSales;
             break;

        case enum2str(PriceGroupType::EndDiscGroup):
             tmpPriceDiscAdmSearchCust.Relation       =  PriceType::EndDiscSales;
             break;
    }

    if (this.valueStr())
    {
        tmpPriceDiscAdmSearchCust.PriceDiscGroupType = str2enum(priceGroup,this.valueStr());
    }

    tmpPriceDiscAdmSearchCust.update();

    return ret;
}

