Method.Source:
[Form]
public class PriceDiscGroup extends FormRun
{
    QueryBuildRange         blockModule;
    QueryBuildRange         blockType;

    ModuleInventCustVend    module;
    PriceGroupType          priceGroupType;
    int                     oldPriceGroupTypeValue;
    string30                inventParameter;

}

void  init()
{
    InventTableModule  inventTableModule;
    PriceDiscTable     priceDiscTable;
    PriceDiscAdmTrans  priceDiscAdmTrans;

    inventParameter = element.args().parm();

    super();

    if (element.args().parmEnumType())
    {
        module = element.args().parmEnum();
    }
    else
    {
        // called from 'go to main table'
        priceDiscType.visible(false);

        if (element.args().record())
        {
            switch (element.args().record().TableId)
            {
                case tableNum(SalesTable),
                     tableNum(CustTable) :
                    module = ModuleInventCustVend::Cust;
                    break;

                case tableNum(PurchTable),
                     tableNum(VendTable) :
                    module = ModuleInventCustVend::Vend;
                    break;

                case tableNum(InventTableModule) :
                    inventTableModule = element.args().record();
                    module = TradeModuleType::newFromModule(inventTableModule.ModuleType).toTradePartyModuleType();
                    break;

                case tableNum(PriceDiscTable) :
                    priceDiscTable = element.args().record();
                    module = priceDiscTable.Module;
                    break;

                case tableNum(PriceDiscAdmTrans) :
                    priceDiscAdmTrans = element.args().record();
                    module = priceDiscAdmTrans.Module;
                    break;

                default :
                    module = ModuleInventCustVend::Cust;
                    element.design().caption("@SYS3088");
            }
        }
    }

    element.setupPriceDiscTypeControl();
    element.setFormCaption();

    switch (module)
    {
        case ModuleInventCustVend::Invent :
            if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
            {
                // link to call item groups from the item
                // setup form.  Need to see only groups that have that item in them.
                if (inventParameter == identifierStr(DiscGroup))
                {
                    PriceDiscGroup_ds.init(element.args().record());
                    PriceDiscType.enabled(false);
                    //disable form when coming from item setup.
                    element.mcrDisableForm();
                }
            }
            break;

        case ModuleInventCustVend::Cust :

            buttonPricePurch.visible          (false);
            buttonLineDiscPurch.visible       (false);
            buttonMultiLineDiscPurch.visible  (false);
            buttonEndDiscPurch.visible        (false);

            // Project sales price
            ProjectsButtonGroup.visible     (true);

            AxdSendButtonGroup.visible      (true);
            break;

        case ModuleInventCustVend::Vend :

            buttonPriceSales.visible          (false);
            buttonLineDiscSales.visible       (false);
            buttonMultiLineDiscSales.visible  (false);
            buttonEndDiscSales.visible        (false);
            break;
    }
    if (element.args().parmEnumType())
    {
        priceGroupType = str2enum(PriceDiscGroup.Type, PriceDiscType.valueStr());

        if (isConfigurationkeyEnabled(configurationKeyNum(Retail))
            && element.args().menuItemName() == menuitemDisplayStr(RetailPriceDiscGroup))
        {
            priceDiscType.visible(false);
            priceGroupType = priceGroupType::PriceGroup;
            // <GEERU>
            PriceDiscGroup_RetailCheckSalesPriceStatus.visible(true);
            // </GEERU>
        }

        element.setFormRanges();
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail))
        && element.args().menuItemName() == menuitemDisplayStr(RetailPriceDiscGroup))
    {
        PriceDiscGroup_RetailPricingPriorityNumber.visible(true);

        if (RetailPricingFeatureToggle::isRetailPricingPropertyFeatureVisible())
        {
            SwitchToPropertyBasedViewButton.visible(true); // Property based view is for Retail && Private preview only.
        }

        if (RetailPricingFeatureToggle::isRetailPricingPropertyFeatureEnabled())
        {
            SwitchToPropertyBasedViewButton.clicked();
        }
    }
}

public void setFormRanges()
{
    QueryBuildDataSource qdbsPriceDiscGroup = priceDiscGroup_ds.query().dataSourceTable(tableNum(PriceDiscGroup));

    blockModule = qdbsPriceDiscGroup.findRange(fieldNum(PriceDiscGroup, Module));
    if (!blockModule)
    {
        blockModule = qdbsPriceDiscGroup.addRange(fieldNum(PriceDiscGroup, Module));
        blockModule.value(queryValue(module));
    }

    blockType = qdbsPriceDiscGroup.findRange(fieldNum(PriceDiscGroup, Type));
    if (blockType)
    {
        this.setlectPriceDiscType(blockType.value());
    }
    else
    {
        blockType = qdbsPriceDiscGroup.addRange(fieldNum(PriceDiscGroup, Type));
        blockType.value(queryValue(priceGroupType));
    }
}

public void setlectPriceDiscType(str _priceGroupTypeStr)
{
    for (int i = 1; i <= priceDiscType.items(); i++)
    {
        priceDiscType.item(i);
        if (_priceGroupTypeStr == priceDiscType.text())
        {
            // item array starts from 1, where selection starts from 0
            priceDiscType.selection(i - 1);
            priceGroupType = str2enum(PriceDiscGroup.Type, _priceGroupTypeStr);
            return;
        }
    }
}

public void setFormCaption()
{
    if (element.args().menuItemType() == MenuItemType::Display)
    {
        switch (element.args().menuItemName())
        {
            case menuitemdisplaystr(PriceDiscGroupCustLineDiscGroup):
            case menuitemdisplaystr(PriceDiscGroupCustMultiLineDiscGroup):
            case menuitemdisplaystr(PriceDiscGroupCustPriceGroup):
                this.design().caption("@SYS71584");
                return;

            case menuitemdisplaystr(PriceDiscGroupProductMultiLineDiscGroup):
            case menuitemdisplaystr(PriceDiscGroupProductLineDiscGroup):
                this.design().caption("@SYS71582");
                return;
        }
    }

    this.design().caption(SysDictMenu::newMenuItem(element.args().menuItemName(), element.args().menuItemType()).label());
}

/// <summary>
/// Disables this form and shows the buttons that will call other
/// forms in disabled mode.
/// </summary>
void mcrDisableForm()
{
    priceDiscGroup_ds.allowCreate(false);
    priceDiscGroup_ds.allowEdit(false);
    priceDiscGroup_ds.allowDelete(false);
    MCRPriceDiscTable_InventLineDiscSales.visible(true);
    MCRPriceDiscTable_InventLineDiscPurch.visible(true);
    ButtonLineDiscSales.visible(false);
    ButtonLineDiscPurch.visible(false);
    MCRPriceDiscGroupItemDisabled.visible(true);
    MCRPriceGroupItems.visible(false);
}

void mcrSetPriceDiscGroupItems()
{
    if (PriceDiscGroup.Module == moduleInventCustVend::Invent
        && inventParameter != identifierStr(DiscGroup)
        && (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter))
        || isConfigurationkeyEnabled(configurationKeyNum(TAMVendorRebate))
        || isConfigurationkeyEnabled(configurationKeyNum(PdsRebate))))
    {
        MCRPriceGroupItems.visible(true);
        if (PriceDiscGroup.Type == PriceGroupType::LineDiscGroup)
        {
            MCRPriceGroupItems.enabled(true);
        }
        else
        {
            MCRPriceGroupItems.enabled(false);
        }
    }
    else
    {
        MCRPriceGroupItems.visible(false);
    }
}

void  reSelect()
{
    QueryBuildDataSource qdbsPriceDiscGroup = priceDiscGroup_ds.query().dataSourceTable(tableNum(PriceDiscGroup));
    blockModule = qdbsPriceDiscGroup.rangeField(fieldNum(PriceDiscGroup, Module));
    blockType = qdbsPriceDiscGroup.rangeField(fieldNum(PriceDiscGroup, Type));

    blockModule.value   (queryValue(module));
    blockType.value     (queryValue(priceGroupType));

    priceDiscGroup_ds.executeQuery();
}

void run()
{
    super();

    if (!element.args().parmEnumType())
    {
        priceGroupType = priceDiscGroup.Type;
    }

    if (element.args().record() is PriceDiscGroup)
    {
        PriceDiscGroup callerRecord = element.args().record();

        if (callerRecord && FormDataUtil::isFormDataSource(callerRecord))
        {
            PriceDiscGroup_ds.research();
            PriceDiscGroup_ds.positionToRecord(callerRecord);
        }
    }

}

void setupPriceDiscTypeControl()
{
    DictEnum dictEnum = DictEnum::newFromName(enumStr(PriceGroupType));
    int enumValues = dictEnum.values();

    priceDiscType.helpText(dictEnum.help());

    if (module == ModuleInventCustVend::Invent)
    {
        priceDiscType.items(3);
    }
    else
    {
        priceDiscType.items(enumValues-1);
    }

    int item = 1;
    Enumerator enum = dictEnum.getEnumerator();
    while (enum.moveNext())
    {
        PriceGroupType value = enum.current();
        if (module != ModuleInventCustVend::Invent ||
            (value != PriceGroupType::PriceGroup &&
             value != PriceGroupType::EndDiscGroup))
        {
            priceDiscType.item(item);
            priceDiscType.text(dictEnum.value2Name(enum2int(value)));
            item++;
        }
    }
}

public void init(InventTable _inventTable = null)
{
    Query                   query;
    QueryBuildDataSource    queryBuildDataSource_PriceDiscGroup;
    QueryBuildDataSource    queryBuildDataSource_MCRPriceDiscGroupItem;

    super();
    // When coming from an item, only show groups that the
    //item belongs to.
    if (module == ModuleInventCustVend::Invent
        && inventParameter == identifierStr(DiscGroup)
        && (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter))
        || isConfigurationkeyEnabled(configurationKeyNum(TAMVendorRebate))
        || isConfigurationkeyEnabled(configurationKeyNum(PdsRebate))))
    {
        query = new Query();

        queryBuildDataSource_PriceDiscGroup = query.addDataSource(tablenum(PriceDiscGroup));
        queryBuildDataSource_MCRPriceDiscGroupItem = queryBuildDataSource_PriceDiscGroup.addDataSource(tableNum(MCRPriceDiscGroupItem));
        queryBuildDataSource_MCRPriceDiscGroupItem.joinMode(JoinMode::ExistsJoin);
        queryBuildDataSource_MCRPriceDiscGroupItem.relations(true);
        queryBuildDataSource_MCRPriceDiscGroupItem.addRange(FieldNum(MCRPriceDiscGroupItem,ItemID)).value(_inventTable.ItemId);

        this.query(query);
    }
}

public int active()
{
    int ret;

    ret = super();

    TradeAgreementsTab.enabled(true);
    SendTab.enabled(true);
    MCRGroupItemsTab.enabled(true);

    element.mcrSetPriceDiscGroupItems();

    if (PriceDiscGroup)
    {
        SalesButtonGroup.enabled(true);
        PurchaseButtonGroup.enabled(true);
        MCRGroupItemButtonGroup.enabled(isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter))
                                    || isConfigurationkeyEnabled(configurationKeyNum(TAMVendorRebate))
                                    || isConfigurationkeyEnabled(configurationKeyNum(PdsRebate)));
    }
    else
    {
        SalesButtonGroup.enabled(false);
        PurchaseButtonGroup.enabled(false);
        MCRGroupItemButtonGroup.enabled(false);
    }

    buttonPriceSales.enabled          (false);
    buttonLineDiscSales.enabled       (false);
    buttonMultiLineDiscSales.enabled  (false);
    buttonEndDiscSales.enabled        (false);

    buttonPricePurch.enabled          (false);
    buttonLineDiscPurch.enabled       (false);
    buttonMultiLineDiscPurch.enabled  (false);
    buttonEndDiscPurch.enabled        (false);

    // Project sales price
    ProjectsButtonGroup.enabled(false);

    switch (priceDiscGroup.Type)
    {
        case PriceGroupType::PriceGroup :
            buttonPriceSales.enabled          (true);
            buttonPricePurch.enabled          (true);

            if (module == ModuleInventCustVend::Cust)
            {
                // Project sales price
                ProjectsButtonGroup.enabled(true);
            }
            break;

        case PriceGroupType::LineDiscGroup :
            buttonLineDiscSales.enabled       (true);
            buttonLineDiscPurch.enabled       (true);
            break;

        case PriceGroupType::MultiLineDiscGroup :
            buttonMultiLineDiscSales.enabled  (true);
            buttonMultiLineDiscPurch.enabled  (true);
            break;

        case PriceGroupType::EndDiscGroup :
            buttonEndDiscSales.enabled        (true);
            buttonEndDiscPurch.enabled        (true);
            break;
    }

    return ret;
}

void  initValue()
{
    super();

    if (element.args().parmEnumType())
    {
        priceDiscGroup.Module   = module;
        priceDiscGroup.Type     = priceGroupType;
    }
}

public void executeQuery()
{
    super();

    //disable all action pane tabs. they will be enabled in active() method (if it is called)
    TradeAgreementsTab.enabled(false);
    SendTab.enabled(false);
    MCRGroupItemsTab.enabled(false);
}

public void clicked()
{
    // The current form and the enhanced form (property based view) should be independent from each other,
    // so overriding the super method to avoid any unexpected parameters carried over.
    // super();

    Args args = element.args();
    args.name(formStr(RetailPriceGroupApplicabilityRule));

    MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(RetailPriceGroupApplicabilityRule), MenuItemType::Display);
    menuFunction.run(args);

    element.close();
}

boolean modified()
{
    boolean ok;
    int num;

    ok = super();
    num = priceDiscType.selection();

    if (num >= 0)
    {
        priceGroupType = str2enum(PriceDiscGroup.Type, this.valueStr());
        oldPriceGroupTypeValue = num;
    }
    else
    {
        priceDiscType.selection(oldPriceGroupTypeValue);
    }

    element.reSelect();

    return ok;
}
