    [Form]
    public class ProcCategoryAddVendor extends FormRun
    {
        EcoResCategory      currentCategory;
        FormDataSource      callerFormDatasource;
        CompanyInfo         selectedLegalEntity;
        Object              caller;

}

void addSelectedVendors()
{
    VendTable      currentVendor;
    VendCategory   tmpVendorCategory;

    tmpVendorCategory.setTmp();

    // Are there any marked records?
    for (currentVendor = getFirstSelection(vendTable_ds);
         currentVendor;
         currentVendor = vendTable_ds.getNext())
    {
        tmpVendorCategory.setTmpData(procCategoryTmpVendorCategory);
        select firstonly tmpVendorCategory
            where tmpVendorCategory.Category        == currentCategory.RecId
                && tmpVendorCategory.VendorAccount  == currentVendor.AccountNum
                && tmpVendorCategory.VendorDataArea == currentVendor.DataAreaId;

        if (!tmpVendorCategory.RecId)
        {
            tmpVendorCategory.clear();
            tmpVendorCategory.initValue();
            tmpVendorCategory.Category          = currentCategory.RecId;
            tmpVendorCategory.VendorAccount     = currentVendor.AccountNum;
            tmpVendorCategory.VendorDataArea    = currentVendor.DataAreaId;
            tmpVendorCategory.doInsert();
        }
    }

    procCategoryTmpVendorCategory_ds.executequery();
}

public void init()
{
    Common          commonForEmptyTitleDS;
    FormRun         callerFormRun = element.args().caller();

    caller = element.args().caller();

    if (!callerFormRun ||
                        !element.args().dataset() ||
                        !element.args().record() ||
                        element.args().dataset() != tablenum(EcoResCategory))
    {
        throw error(strfmt("@SYS22678",element.name()));
    }

    currentCategory      = element.args().record();
    selectedLegalEntity  = caller.getSelectedLegalEntity();
    callerFormDatasource = callerFormRun.dataSource();

    super();

    // To clear dynamic Title dataSource, need to pass either null or empty table buffer.
    element.args().record(commonForEmptyTitleDS);
}

void removeSelectedVendors()
{
    // Are there any marked records?
    ttsbegin;
    if (procCategoryTmpVendorCategory_ds.anyMarked())
    {
        procCategoryTmpVendorCategory_ds.deleteMarked();
    }
    else if (getFirstSelection(procCategoryTmpVendorCategory_ds))
    {
        procCategoryTmpVendorCategory_ds.delete();
    }
    ttscommit;
    procCategoryTmpVendorCategory_ds.executequery();
}

/// <summary>
/// Sets the state of the controls on the depending on the formdatasource record selected.
/// </summary>
/// <param name="_formDataSource">
/// The form dtaSource.
/// </param>
/// <param name="_control">
/// The FormControl that needs to be disabled based on the FormdataSource selected records.
/// </param>
void setControlState(FormDataSource _formDataSource, FormControl _control)
{
    Common selectedRecord;

    selectedRecord = getFirstSelection(_formDataSource);
    _control.enabled(selectedRecord.RecId);
}

void updateVendorCategory()
{
    ttsbegin;
    while select procCategoryTmpVendorCategory
    {
        // this takes care of find or create VendorCategoryStatus as well
        VendCategory::findOrCreateVendorCategory(
            procCategoryTmpVendorCategory.VendorAccount,
            procCategoryTmpVendorCategory.Category,
            procCategoryTmpVendorCategory.VendorDataArea
        );
    }
    ttscommit;
    caller.refreshVendorsGrid();
}

public void init()
{
    super();
    //this is for: not to show already associated records
    //top grid datasource = (VendTable NotExistsJoin  (VendCategory data filtered by current category ) )
    this.query().dataSourceTable(tableNum(VendCategory)).addRange(fieldNum(VendCategory, Category)).value(queryValue(currentCategory.RecId));
}
