Method.Source:
[Form]
public class ProcCategoryGuideline extends FormRun
{
    EcoResCategory                  currentCategory;
    ProcCategoryGuidelineParameter  categoryGuidelineParameter;
}

public void init()
{
    if (!element.args() ||
        !element.args().record() ||
        !element.args().dataset() ||
         element.args().dataset() != tablenum(EcoResCategory))
    {
        throw error(strfmt("@SYS22678", element.name()));
    }

    currentCategory.data(element.args().record());

    categoryGuidelineParameter = element.args().parmObject();
    if (!categoryGuidelineParameter)
    {
        categoryGuidelineParameter = new ProcCategoryGuidelineParameter();
    }

    super();

    element.setRanges();
}

/// <summary>
///    This function applies filter on Category, Language and DataArea fields based on the parameters passed by the caller.
/// </summary>
/// <remarks>
///    1. The general tab displays basic category information pertaining to the procurement category passed in by the caller.
///    2. The category guidelines and details fast tab displays the HTML content that will be created (as a category page)
///       in the category hierarchy management UI. If category guidelines have not been defined for the category,
///       then the fast tab will display blank.
///    3. The Vendor tab displays vendor information pertaining to the procurement category for specific buying
///       organization and/or operating unit that have been provided by the caller of the form.
///       If only buying organization is provided, then show vendors across all the operating units that are
///       associated with the buying organization.
/// </remarks>
private void setRanges()
{
    ProcCategoryPageTranslation   categoryPageTranslation;
    QueryBuildDataSource          qbdsCompanyInfo;
    RefRecId                      buyingLegalEntity;
    LanguageId                    languageId;

    languageId              = categoryGuidelineParameter.parmLanguageId();
    buyingLegalEntity       = categoryGuidelineParameter.parmBuyingLegalEntity();

    //get the details pertaining to the specified category
    ecoResCategory_ds.query().dataSourceTable(tablenum(EcoResCategory)).clearDynalinks();
    ecoResCategory_ds.query().dataSourceTable(tablenum(EcoResCategory)).addRange(fieldnum(EcoResCategory, RecId)).value(queryValue(currentCategory.RecId));

    //get the category description for the specified language
    ecoResCategoryTranslation_ds.query().dataSourceTable(tablenum(EcoResCategoryTranslation)).addRange(fieldnum(EcoResCategoryTranslation, LanguageId)).value(queryValue(languageId));

    //get category page applicable for the specified buyingLegalEntity and receivingOperatingUnit
    categoryPageTranslation = EcoResCategory::getProcCategoryPage(currentCategory.RecId, buyingLegalEntity, languageId);
    htmlPageContent.parmHtml(categoryPageTranslation.HtmlText);

    //get all vendors for the specified category
    ecoResCategory_ForVendors_ds.query().dataSourceTable(tablenum(EcoResCategory)).addRange(fieldnum(EcoResCategory, RecId)).value(queryValue(currentCategory.RecId));

    //Filter vendors for the specified legal entity
    qbdsCompanyInfo = companyInfo_ds.query().dataSourceTable(tablenum(CompanyInfo));
    qbdsCompanyInfo.clearLinks();
    qbdsCompanyInfo.clearDynalinks();
    qbdsCompanyInfo.joinMode(JoinMode::InnerJoin);
    qbdsCompanyInfo.addRange(fieldnum(CompanyInfo, RecId)).value(queryValue(buyingLegalEntity));

    vendTable_ds.query().clearCompanyRange();
    vendTable_ds.query().addCompanyRange(queryValue(CompanyInfo::findRecId(buyingLegalEntity).DataArea));
}
