private void deleteVendorCategoryWithNoVendProcurementCategoryStatus(VendCategoryRecId _vendorCategoryRecId)
{
    VendProcurementCategoryStatus   tmpVendCategoryStatus;
    VendCategory vendorCategory;

    select firstonly tmpVendCategoryStatus
                    where tmpVendCategoryStatus.VendorCategory == _vendorCategoryRecId;

    if (!tmpVendCategoryStatus)
    {
        select forupdate vendorCategory
                        where vendorCategory.RecId == _vendorCategoryRecId;
        vendorCategory.delete();
    }
}

