{
    super();

    #ISOCountryRegionCodes

    legalEntityMap                      = new Map(Types::Int64, Types::String);
    operatingUnitMap                    = new Map(Types::Int64, Types::String);

    currentProcurementCategoryHierarchy = element.initializeProcurementCategoryHierarchy();

