/// <summary>
/// Initializes procurement <c>EcoResCategoryHierarchy</c> record.
/// </summary>
/// <returns>An instance of <c>EcoResCategoryHierarchy</c> record.</returns>
[Replaceable]
protected EcoResCategoryHierarchy initializeProcurementCategoryHierarchy()
{
    return EcoResCategoryHierarchy::find(EcoResCategoryHierarchyRole::getHierarchiesByRole(EcoResCategoryNamedHierarchyRole::Procurement).CategoryHierarchy);
}

void initializeHelper()
{
    QueryBuildRange         range;
    EcoResCategory          root;
    EcoResCategoryLookupParameters lookupParameters = new EcoResCategoryLookupParameters(false, false, false);

    root = EcoResCategory::getRoot(currentProcurementCategoryHierarchy.RecId);
    if (!root)
    {
        throw Global::error("@SYS134281");
    }

    range = ecoResCategoryHierarchy_ds.query().dataSourceTable(tablenum(EcoResCategoryHierarchy)).addRange(fieldnum(EcoResCategoryHierarchy, RecId));
    range.value( queryValue(currentProcurementCategoryHierarchy.RecId ) );
    ecoResCategoryHierarchy_ds.executeQuery();

    range = ecoResCategory_ds.query().dataSourceTable(tablenum(EcoResCategory)).addRange(fieldnum(EcoResCategory, CategoryHierarchy));
    range.value( queryValue(currentProcurementCategoryHierarchy.RecId ) );
    ecoResCategory_ds.executeQuery();

    hierarchyTree = new EcoResCategoryTreeDatasource(  ecoResCategory_ds,
                                                       categoryTreeCtrl,
                                                       fieldnum(EcoResCategory, RecId),
                                                       fieldnum(EcoResCategory, ParentCategory),
                                                       fieldnum(EcoResCategory, Name),
                                                       false /* No ID in description */,
                                                       true,
                                                       fieldnum(EcoResCategory, Code),
                                                       currentProcurementCategoryHierarchy,
                                                       ecoResCategoryHierarchy_ds,
                                                       lookupParameters);

    hierarchyTree.initRoot(root.Name, root.RecId, hierarchyTree.image() );

    element.design().caption(strfmt("@SYS340428", ecoResCategoryHierarchy.Name));
}

public void rebuildCategoryHierarchyTree()
{
    element.initializeHelper();
}

public void insertTypeRecord(AttributeDataType _dataType)
{
    Map selectedFormDataSources = new Map(Types::String, Types::String);

    switch (_dataType)
    {
        case AttributeDataType::Currency:
            selectedFormDataSources.insert(ecoResValue_ds.name(), tablestr(EcoResCurrencyValue));
            break;
        case AttributeDataType::DateTime:
            selectedFormDataSources.insert(ecoResValue_ds.name(), tablestr(EcoResDateTimeValue));
            break;
        case AttributeDataType::Decimal:
            selectedFormDataSources.insert(ecoResValue_ds.name(), tablestr(EcoResFloatValue));
            break;
        case AttributeDataType::Integer:
            selectedFormDataSources.insert(ecoResValue_ds.name(), tablestr(EcoResIntValue));
            break;
        case AttributeDataType::Text:
            selectedFormDataSources.insert(ecoResValue_ds.name(), tablestr(EcoResTextValue));
            break;
        case AttributeDataType::TrueFalse:
            selectedFormDataSources.insert(ecoResValue_ds.name(), tablestr(EcoResBooleanValue));
            break;
        default:
            return;
    }
    ecoResAttributeValue_ds.createTypes(selectedFormDataSources);
}

void lookupTaxGroup(FormControl _formControl)
{
    Query                   query = new Query();
    SysTableLookup          sysTableLookup;

    sysTableLookup = SysTableLookup::newParameters(tablenum(TaxItemGroupHeading), _formControl);

    query.addDataSource(tablenum(TaxItemGroupHeading));

    sysTableLookup.addLookupfield(fieldnum(TaxItemGroupHeading, TaxItemGroup));
    sysTableLookup.addLookupfield(fieldnum(TaxItemGroupHeading, Name));
    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

public void refreshCategoryAttributeList()
{
    currentCategoryInstanceValue = EcoResCategoryInstanceValue::insertCategoryInstance(ecoResCategory.RecId, selectedLegalEntity.RecId);
    ecoResCategory_ForCategoryAttributes_ds.query().dataSourceTable(tablenum(EcoResCategory)).clearRanges();
    ecoResCategoryAttribute_ds.query().dataSourceTable(tablenum(EcoResCategoryAttribute)).clearRanges();

    ecoResCategory_ForCategoryAttributes_ds.query().dataSourceTable(tablenum(EcoResCategory)).addRange(fieldnum(EcoResCategory, RecId));

    if (ecoResCategory.IsCategoryAttributesInherited == NoYes::No )
    {
        ecoResCategory_ForCategoryAttributes_ds.query().dataSourceTable(tablenum(EcoResCategory)).findRange(fieldnum(EcoResCategory, RecId)).value(queryValue(ecoResCategory.RecId));
    }
    else
    {
        EcoResCategory::setCategoryRangeBasedOnInheritance(ecoResCategory_ForCategoryAttributes_ds, ecoResCategory, fieldnum(EcoResCategory, IsCategoryAttributesInherited));
    }

    ecoResCategoryAttribute_ds.query().dataSourceTable(tablenum(EcoResCategoryAttribute)).addRange(fieldnum(EcoResCategoryAttribute, Modifier)).value(queryValue(EcoResCategoryAttributeModifier::Category));
    ecoResCategoryAttribute_ds.executeQuery();
    ecoResAttributeValue_ds.executeQuery();

    ecoResAttributeType = EcoResAttributeType::find(ecoResAttribute.AttributeType);
    element.showValueField(ecoResAttributeType.DataType);
}

