/// <summary>
/// Rebuilds the queries for the EcoResCategory_ForProductAttributesInh and
/// EcoResCategory_ForProductInh datasources, then refreshes the grid listing all
/// inherited product attributes of the selected category.
/// </summary>
public void refreshInheritedProductAttributeListGrid()
{
    QueryBuildDataSource qbdsCategoryInherited            = ecoResCategory_InheritedProduct_ds.query().dataSourceTable(tablenum(EcoResCategory));
    QueryBuildDataSource qbdsCategoryAttributesInherited  = ecoResCategoryAttribute_InheritedProduct_ds.query().dataSourceTable(tablenum(EcoResCategoryAttribute));

    // set the range for the category attribute modifier to product
    qbdsCategoryAttributesInherited.clearRanges();
    qbdsCategoryAttributesInherited.addRange(fieldnum(EcoResCategoryAttribute, Modifier)).value(queryValue(EcoResCategoryAttributeModifier::Product));

    // set the range for the category when inherit attributes is checked
    qbdsCategoryInherited.clearRanges();
    if (NoYes::Yes == procCategoryModifier.IsProductAttributesInherited)
    {
        ProcCategoryModifier::setCategoryRangeBasedOnInheritance(ecoResCategory_InheritedProduct_ds, ecoResCategory, fieldnum(ProcCategoryModifier, IsProductAttributesInherited));

        // don't include the current selected category
        qbdsCategoryInherited.addRange(fieldnum(EcoResCategory, RecId)).value(strfmt('(%1 != %2)', fieldstr(EcoResCategory, RecId), queryValue(ecoResCategory.RecId)));
    }
    else
    {
        // No inherited attributes
        qbdsCategoryInherited.addRange(fieldnum(EcoResCategory, RecId)).value(SysQuery::value(0));
    }

    // finally, refresh UI
    ecoResCategoryAttribute_InheritedProduct_ds.executeQuery();
}

